                "schema": {
                  "$ref": "#/components/schemas/jsonError"
                }
              }
            },
            "description": "jsonError"
          }
        },
        "summary": "Check Who the Current HTTP Session Belongs To",
        "tags": [
          "v0alpha2"
        ]
      }
