            "content": {
              "text/xml": {
                "schema": {
                  "$ref": "#/components/schemas/GetBucketReplicationOutput"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "Bucket",
            "in": "path",
            "required": true,
            "description": "The bucket name for which to get the replication information.",
            "schema": { "type": "string" }
          },
          {
            "name": "x-amz-expected-bucket-owner",
            "in": "header",
            "required": false,
            "description": "The account id of the expected bucket owner. If the bucket is owned by a different account, the request will fail with an HTTP <code>403 (Access Denied)</code> error.",
            "schema": { "type": "string" }
          },
          {
