        },
        "parameters": [
          {
            "name": "Bucket",
            "in": "path",
            "required": true,
            "description": "The name of the Amazon S3 bucket whose <code>PublicAccessBlock</code> configuration you want to retrieve. ",
            "schema": { "type": "string" }
          },
          {
            "name": "x-amz-expected-bucket-owner",
            "in": "header",
            "required": false,
            "description": "The account id of the expected bucket owner. If the bucket is owned by a different account, the request will fail with an HTTP <code>403 (Access Denied)</code> error.",
            "schema": { "type": "string" }
