        "/api/comm-links/reverse-image-link-search": {
            "post": {
                "tags": [
                    "Comm-Links",
                    "RSI-Website"
                ],
                "operationId": "1d2dbc6d15a4bf23898423f8908b5479",
                "requestBody": {
                    "description": "Url to an image hosted on (media.)robertsspaceindustries.com",
                    "required": true,
                    "content": {
                        "application/json": {
                            "schema": {
                                "schema": "url",
                                "type": "json"
                            },
                            "example": "{\"url\": \"https://robertsspaceindustries.com/i/cc75a45005a236c6e015dfc2782a2f55ed1e84a2/ADdPNihJzmPbNuTnFsH1DqUeqBRpXdSXVVtgJTyDDgscGKrzJuoFjResiiucPBBDeyrBscqRyZz4qxNsSbWvqUwdG/alien-week-2022-front.webp\"}"
                        }
                    }
                },
                "responses": {
                    "200": {
                        "description": "List of Comm-Links that use that image",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "array",
                                    "items": {
                                        "$ref": "#/components/schemas/comm_link_link"
                                    }
                                }
                            }
                        }
                    },
                    "404": {
                        "description": "No Comm-Link found."
                    }
                }
            }
        },
        "/api/comm-links/reverse-image-search": {
            "post": {
                "tags": [
                    "Comm-Links",
                    "RSI-Website"
                ],
                "operationId": "bc65a596111a1b4dd2d8651c9d13813b",
                "parameters": [
                    {
                        "name": "similarity",
                        "in": "query",
                        "required": true,
                        "schema": {
                            "schema": "image_similarity",
                            "type": "integer",
                            "maximum": 100,
                            "minimum": 1
                        }
                    },
                    {
                        "name": "method",
                        "in": "query",
                        "required": true,
                        "schema": {
                            "schema": "image_search_method",
                            "collectionFormat": "csv",
                            "default": "perceptual",
                            "enum": [
                                "perceptual",
                                "difference",
                                "average"
                            ]
                        }
                    }
                ],
                "requestBody": {
                    "required": true,
                    "content": {
                        "application/octet-stream": {
                            "schema": {
                                "schema": "image",
                                "description": "The image to reverse-search",
                                "type": "string",
                                "format": "binary"
                            }
                        }
                    }
                },
                "responses": {
                    "200": {
                        "description": "List of Comm-Links that use that image",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "array",
                                    "items": {
                                        "$ref": "#/components/schemas/comm_link_link"
                                    }
                                }
                            }
                        }
                    },
                    "404": {
                        "description": "No Comm-Link found."
                    }
                }
            }
        },
