{
	"openapi": "3.0.1",
	"servers": [
		{
			"description": "API para manipulação do Produto do MRP",
			"url": "{{host}}/api/pcp/v1",
			"variables": {
				"serverUrl": {
					"default": "localhost"
				},
				"serverHttpPort": {
					"default": "8040"
				}
			}
		}
