							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/MRPProduct_1_000.json#/definitions/PagedMRPProduct"
								}
							}
						}
					},
					"207": {
						"description": "Operação realizada parcialmente com sucesso",
