					},
					{
						"name": "branchId",
						"in": "query",
						"description": "Código da filial",
						"required": false,
						"schema": {
							"type": "string"
						}
					},
					{
						"name": "product",
						"in": "query",
						"description": "Código do produto",
						"required": false,
