{
	"openapi": "3.0.1",
	"servers": [
		{
			"description": "API para a entidade turmas (classes) para produtos TOTVS",
			"url": "{{host}}/api/rh/v1",
			"variables": {
				"serverUrl": {
					"default": "localhost"
				},
				"serverHttpPort": {
					"default": "8051"
				}
			}
		}
	],
	"info": {
		"description": "API para a entidade turmas (classes) para produtos TOTVS",
		"version": "1.000",
		"title": "Turmas",
		"contact": {
			"name": "T-Talk",
			"url": "API.Totvs.com.br",
			"email": "comiteintegracao@totvs.com.br"
		},
		"x-totvs": {
			"messageDocumentation": {
				"name": "Classes",
				"description": "Turma",
				"segment": "Recursos Humanos"
			},
			"productInformation": [
				{
					"product": "RM",
					"contact": "rm_FW@totvs.com.br",
					"description": "Listagem das turmas"
				}
			]
		}
	},
	"paths": {
		"/classes/{id}": {
			"get": {
				"tags": [
					"Classes"
				],
				"summary": "Retorna dados de tabela da turma especificada",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponivel com todos parametros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"description": "Retorna dados de tabela da turma especificada",
				"operationId": "getClassesId",
				"parameters": [
					{
						"$ref": "#/components/parameters/ClassesId"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"responses": {
					"200": {
						"description": "Operação executada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Classes_1_000.json#/definitions/ClassesInfo"
								}
							}
						}
					},
					"400": {
						"description": "Erro na execução da operação",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			},
			"delete": {
				"tags": [
					"Classes"
				],
				"summary": "Exclui turma",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponivel com todos parametros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"description": "Exclui turma",
				"operationId": "DeleteClassesId",
				"parameters": [
					{
						"$ref": "#/components/parameters/ClassesId"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"responses": {
					"200": {
						"description": "Operação executada com sucesso"
					},
					"400": {
						"description": "Erro na execução da operação",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			},
			"put": {
				"tags": [
					"Classes"
				],
				"summary": "Atualiza um registro existente de tabela de turmas",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponivel com todos parametros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"description": "Atualiza um registro existente de tabela de turmas. Caso não seja informado alguma propriedade para ser atualizada, está será considerada nula.",
				"operationId": "putClassesId",
				"parameters": [
					{
						"$ref": "#/components/parameters/ClassesId"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"requestBody": {
					"description": "Objeto para atualizar o registro de tabela de turmas",
					"content": {
						"application/json": {
							"schema": {
								"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Classes_1_000.json#/definitions/ClassesInfo"
							}
						}
					}
				},
				"responses": {
					"200": {
						"description": "Turma atualizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Classes_1_000.json#/definitions/ClassesInfo"
								}
							}
						}
					},
					"400": {
						"description": "Erro na execução da operação",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			}
		},
		"/classes": {
			"get": {
				"tags": [
					"Classes"
				],
				"summary": "Retorna todas as turmas",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponível com todos os parâmetros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"description": "Retorna todos as turmas",
				"operationId": "getClasses",
				"parameters": [
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Order"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Page"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/PageSize"
					},
					{
						"$ref": "#/components/parameters/ClassesIds"
					},
					{
						"$ref": "#/components/parameters/Names"
					}
				],
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Classes_1_000.json#/definitions/ClassesInfo"
								}
							}
						}
					},
					"400": {
						"description": "Erro no momento da listagem das turmas",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			},
			"post": {
				"tags": [
					"Classes"
				],
				"summary": "Inclui um novo registro na tabela de turmas",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponivel com todos parametros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"parameters": [
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"description": "Inclui um novo registro na tabela de turmas",
				"operationId": "postClasses",
				"requestBody": {
					"description": "Objeto para atualizar o registro de tabela de turmas",
					"content": {
						"application/json": {
							"schema": {
								"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Classes_1_000.json#/definitions/ClassesInfo"
							}
						}
					}
				},
				"responses": {
					"200": {
						"description": "Operação executada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Classes_1_000.json#/definitions/ClassesInfo"
								}
							}
						}
					},
					"400": {
						"description": "Erro na execução da operação",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			}
		}
	},
	"components": {
		"parameters": {
			"ClassesId": {
				"name": "id",
				"in": "path",
				"required": true,
				"description": "Identificador Único representando a turma",
				"schema": {
					"type": "string"
				}
			},
			"ClassesIds": {
				"name": "classesIds",
				"in": "query",
				"description": "Códigos das turmas retornados pelo get separado por vírgula (,) ",
				"required": false,
				"example": "param1,param2",
				"schema": {
					"type": "string"
				}
			},
			"Names": {
				"name": "name",
				"in": "query",
				"description": "Busca por nome da turma",
				"required": false,
				"example": "Usuário de busca",
				"schema": {
					"type": "string"
				}
			}
		},
		"schemas": {}
	}
}
