{
  "openapi": "3.0.3",
  "info": {
    "version": "5.5.0",
    "title": "Pinterest REST API",
    "description": "Pinterest's REST API",
    "contact": {
      "name": "Pinterest, Inc.",
      "url": "https://developers.pinterest.com/",
      "email": "pinterest-api@pinterest.com"
    },
    "license": {
      "name": "MIT",
      "url": "https://spdx.org/licenses/MIT"
    },
    "termsOfService": "https://developers.pinterest.com/terms/"
  },
  "servers": [
    {
      "url": "https://api.pinterest.com/v5"
    }
  ],
  "tags": [
    {
      "name": "ad_accounts",
      "description": "View analytical information about advertising.\n\n\nNote: If the current operation_user_account (defined by the access token)\nhas access to another user's Ad Accounts via\n<a href='/docs/reference/business-access/'>Pinterest Business Access</a>,\nyou can modify your request to use the current operation_user_account's\npermissions to those Ad Accounts by including the ad_account_id in the path\nparameters for the request (e.g. .../?ad_account_id=12345&...)."
    },
    {
      "name": "ad_groups",
      "description": "View, create or update ad groups."
    },
    {
      "name": "ads",
      "description": "View, create or update ads."
    },
    {
      "name": "audiences",
      "description": "View, create, or update audiences."
    },
    {
      "name": "boards",
      "description": "View, create, update, or delete information about boards."
    },
    {
      "name": "campaigns",
      "description": "View, create or update campaigns."
    },
    {
      "name": "catalogs",
      "description": "Manage information about shopping product catalogs and items."
    },
    {
      "name": "customer_lists",
      "description": "View, create, or update customer lists."
    },
    {
      "name": "keywords",
      "description": "View, create or update keywords."
    },
    {
      "name": "media",
      "description": "Register and manage media uploads."
    },
    {
      "name": "oauth",
      "description": "Generating and refreshing OAuth access tokens."
    },
    {
      "name": "pins",
      "description": "View, create, update, or delete information about Pins."
    },
    {
      "name": "product_group_promotions",
      "description": "Product Group Promotion"
    },
    {
      "name": "resources",
      "description": "View, create or update resources."
    },
    {
      "name": "terms",
      "description": "Get related and suggested terms."
    },
    {
      "name": "terms_of_service",
      "description": "Get Terms Of Service"
    },
    {
      "name": "user_account",
      "description": "Read-only information about user accounts associated with a given access token."
    }
  ],
  "paths": {
    "/oauth/token": {
      "post": {
        "summary": "Generate OAuth access token",
        "description": "Generate an OAuth access token by using an authorization code or a refresh token.\n\nSee <a href='/docs/getting-started/authentication/'>Authentication</a> for more.",
        "tags": [
          "oauth"
        ],
        "operationId": "oauth/token",
        "security": [
          {
            "basic": []
          }
        ],
        "x-ratelimit-category": "org_read",
        "x-sandbox": {
          "enabled": false
        },
        "requestBody": {
          "description": "Generate an OAuth access token.",
          "required": true,
          "content": {
            "application/x-www-form-urlencoded": {
              "schema": {
                "$ref": "#/components/schemas/OauthAccessTokenRequest"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/OauthAccessTokenResponse"
                }
              }
            }
          },
          "default": {
            "description": "Unexpected error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      }
    },
    "/user_account": {
      "get": {
        "summary": "Get user account",
        "description": "Get account information for the \"operation user_account\"\n- By default, the \"operation user_account\" is the token user_account.\n\nIf using Business Access: Specify an ad_account_id to use the owner of that ad_account as the \"operation user_account\". See <a href='/docs/reference/business-access/'>Understanding Business Access</a> for more information.",
        "tags": [
          "user_account"
        ],
        "operationId": "user_account/get",
        "security": [
          {
            "pinterest_oauth2": [
              "user_accounts:read"
            ]
          }
        ],
        "x-ratelimit-category": "org_read",
        "x-sandbox": {
          "enabled": false
        },
        "parameters": [
          {
            "$ref": "#/components/parameters/query_ad_account_id"
          }
        ],
        "responses": {
          "200": {
            "description": "response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Account"
                }
              }
            }
          },
          "403": {
            "description": "Not authorized to access the user account.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                },
                "example": {
                  "code": 403,
                  "message": "Not authorized to access the user account."
                }
              }
            }
          },
          "default": {
            "description": "Unexpected error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      }
    },
    "/user_account/analytics": {
      "get": {
        "summary": "Get user account analytics",
        "description": "Get analytics for the \"operation user_account\"\n- By default, the \"operation user_account\" is the token user_account.\n\nOptional: Business Access: Specify an ad_account_id to use the owner of that ad_account as the \"operation user_account\".",
        "tags": [
          "user_account"
        ],
        "operationId": "user_account/analytics",
        "security": [
          {
            "pinterest_oauth2": [
              "user_accounts:read"
            ]
          }
        ],
        "x-ratelimit-category": "org_analytics",
        "x-sandbox": {
          "enabled": false
        },
        "parameters": [
          {
            "$ref": "#/components/parameters/query_start_date"
          },
          {
            "$ref": "#/components/parameters/query_end_date"
          },
          {
            "$ref": "#/components/parameters/query_from_claimed_content"
          },
          {
            "$ref": "#/components/parameters/query_pin_format"
          },
          {
            "$ref": "#/components/parameters/query_app_types"
          },
          {
            "$ref": "#/components/parameters/query_metric_types"
          },
          {
            "$ref": "#/components/parameters/query_split_field_user_account"
          },
          {
            "$ref": "#/components/parameters/query_ad_account_id"
          }
        ],
        "responses": {
          "200": {
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/AnalyticsResponse"
                }
              }
            },
            "description": "Success"
          },
          "400": {
            "description": "Invalid user accounts analytics parameters.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                },
                "example": {
                  "code": 400,
                  "message": "Invalid user accounts analytics parameters."
                }
              }
            }
          },
          "403": {
            "description": "Not authorized to access the user account analytics.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                },
                "example": {
                  "code": 403,
                  "message": "Not authorized to access the user account analytics."
                }
              }
            }
          },
          "default": {
            "description": "Unexpected error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      }
    },
    "/user_account/analytics/top_pins": {
      "get": {
        "summary": "Get user account top pins analytics",
        "description": "Gets analytics data about a user's top pins (limited to the top 50).\n- By default, the \"operation user_account\" is the token user_account.\n\nOptional: Business Access: Specify an ad_account_id to use the owner of that ad_account as the \"operation user_account\".",
        "tags": [
          "user_account"
        ],
        "operationId": "user_account/analytics/top_pins",
        "security": [
          {
            "pinterest_oauth2": [
              "pins:read",
              "user_accounts:read"
            ]
          }
        ],
        "x-ratelimit-category": "org_analytics",
        "x-sandbox": {
          "enabled": false
        },
        "parameters": [
          {
            "$ref": "#/components/parameters/query_start_date"
          },
          {
            "$ref": "#/components/parameters/query_end_date"
          },
          {
            "$ref": "#/components/parameters/query_sort_by"
          },
          {
            "$ref": "#/components/parameters/query_from_claimed_content"
          },
          {
            "$ref": "#/components/parameters/query_pin_format"
          },
          {
            "$ref": "#/components/parameters/query_app_types"
          },
          {
            "$ref": "#/components/parameters/query_metric_types"
          },
          {
            "$ref": "#/components/parameters/query_num_of_pins"
          },
          {
            "$ref": "#/components/parameters/query_created_in_last_n_days"
          },
          {
            "$ref": "#/components/parameters/query_ad_account_id"
          }
        ],
        "responses": {
          "200": {
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/TopPinsAnalyticsResponse"
                }
              }
            },
            "description": "Success"
          },
          "403": {
            "description": "Not authorized to access the user account analytics.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                },
                "example": {
                  "code": 403,
                  "message": "Not authorized to access the user account analytics."
                }
              }
            }
          },
          "default": {
            "description": "Unexpected error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      }
    },
    "/user_account/analytics/top_video_pins": {
      "get": {
        "summary": "Get user account top video pins analytics",
        "description": "Gets analytics data about a user's top video pins (limited to the top 50).\n- By default, the \"operation user_account\" is the token user_account.\n\nOptional: Business Access: Specify an ad_account_id to use the owner of that ad_account as the \"operation user_account\".",
        "tags": [
          "user_account"
        ],
        "operationId": "user_account/analytics/top_video_pins",
        "security": [
          {
            "pinterest_oauth2": [
              "pins:read",
              "user_accounts:read"
            ]
          }
        ],
        "x-ratelimit-category": "org_analytics",
        "x-sandbox": {
          "enabled": false
        },
        "parameters": [
          {
            "$ref": "#/components/parameters/query_start_date"
          },
          {
            "$ref": "#/components/parameters/query_end_date"
          },
          {
            "$ref": "#/components/parameters/query_video_pin_sort_by"
          },
          {
            "$ref": "#/components/parameters/query_from_claimed_content"
          },
          {
            "$ref": "#/components/parameters/query_pin_format"
          },
          {
            "$ref": "#/components/parameters/query_app_types"
          },
          {
            "$ref": "#/components/parameters/query_video_pin_metric_types"
          },
          {
            "$ref": "#/components/parameters/query_num_of_pins"
          },
          {
            "$ref": "#/components/parameters/query_created_in_last_n_days"
          },
          {
            "$ref": "#/components/parameters/query_ad_account_id"
          }
        ],
        "responses": {
          "200": {
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/TopVideoPinsAnalyticsResponse"
                }
              }
            },
            "description": "Success"
          },
          "403": {
            "description": "Not authorized to access the user account analytics.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                },
                "example": {
                  "code": 403,
                  "message": "Not authorized to access the user account analytics."
                }
              }
            }
          },
          "default": {
            "description": "Unexpected error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      }
    },
    "/pins": {
      "post": {
        "summary": "Create Pin",
        "description": "Create a Pin on a board or board section owned by the \"operation user_account\".\n\nNote: If the current \"operation user_account\" (defined by the access token) has access to another user's Ad Accounts via Pinterest Business Access, you can modify your request to make use of the current operation_user_account's permissions to those Ad Accounts by including the ad_account_id in the path parameters for the request (e.g. .../?ad_account_id=12345&...).\n\n- This function is intended solely for publishing new content created by the user. If you are interested in saving content created by others to your Pinterest boards, sometimes called 'curated content', please use our <a href='/docs/add-ons/save-button'>Save button</a> instead. For more tips on creating fresh content for Pinterest, review our <a href='/docs/solutions/content-apps'>Content App Solutions Guide</a>.\n\n<strong><a href='/docs/solutions/content-apps/#creatingvideopins'>Learn more</a></strong> about video Pin creation.",
        "tags": [
          "pins"
        ],
        "operationId": "pins/create",
        "security": [
          {
            "pinterest_oauth2": [
              "boards:read",
              "boards:write",
              "pins:read",
              "pins:write"
            ]
          }
        ],
        "x-ratelimit-category": "org_write",
        "x-sandbox": {
          "enabled": false
        },
        "requestBody": {
          "description": "Create a new Pin.",
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/Pin"
              }
            }
          }
        },
        "responses": {
          "201": {
            "description": "Successful pin creation.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Pin"
                }
              }
            }
          },
          "400": {
            "description": "Invalid Pin parameters response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                },
                "examples": {
                  "InvalidPinUrl": {
                    "value": {
                      "code": 1,
                      "message": "Whoops! It looks like you entered an invalid URL. Try creating a Pin again with a valid URL."
                    }
                  }
                }
              }
            }
          },
          "403": {
            "description": "The Pin's image is too small, too large or is broken",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                },
                "examples": {
                  "PinImageTooSmall": {
                    "value": {
                      "code": 233,
                      "message": "Your image is too small. Please choose a larger image and try again."
                    }
                  },
                  "PinImageBroken": {
                    "value": {
                      "code": 235,
                      "message": "Sorry, this image is broken. Please pick a different image."
                    }
                  }
                }
              }
            }
          },
          "404": {
            "description": "Board or section not found",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                },
                "examples": {
                  "BoardNotFound": {
                    "value": {
                      "code": 40,
                      "message": "Board not found."
                    }
                  },
                  "BoardSectionNotFound": {
                    "value": {
                      "code": 2031,
                      "message": "Sorry! We couldn't find this board section."
                    }
                  }
                }
              }
            }
          },
          "429": {
            "description": "This request exceeded a rate limit. This can happen if the client exceeds one\nof the published rate limits or if multiple write operations are applied to\nan object within a short time window.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                },
                "example": {
                  "code": 8,
                  "message": "This request exceeded a rate limit. This can happen if the client exceeds one\nof the published rate limits or if multiple write operations are applied to\nan object within a short time window."
                }
              }
            }
          },
          "default": {
            "description": "Unexpected error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      }
    },
    "/pins/{pin_id}": {
      "get": {
        "summary": "Get Pin",
        "description": "Get a Pin owned by the \"operation user_account\" - or on a group board that has been shared with this account.\n- By default, the \"operation user_account\" is the token user_account.\n\nOptional: Business Access: Specify an <code>ad_account_id</code> (obtained via <a href='/docs/api/v5/#operation/ad_accounts/list'>List ad accounts</a>) to use the owner of that ad_account as the \"operation user_account\". In order to do this, the token user_account must have one of the following <a href=\"https://help.pinterest.com/en/business/article/share-and-manage-access-to-your-ad-accounts\">Business Access</a> roles on the ad_account:\n\n- For Pins on public or protected boards: Owner, Admin, Analyst, Campaign Manager.\n- For Pins on secret boards: Owner, Admin.",
        "tags": [
          "pins"
        ],
        "operationId": "pins/get",
        "security": [
          {
            "pinterest_oauth2": [
              "boards:read",
              "pins:read"
            ]
          }
        ],
        "x-ratelimit-category": "org_read",
        "x-sandbox": {
          "enabled": false
        },
        "parameters": [
          {
            "$ref": "#/components/parameters/path_pin_id"
          },
          {
            "$ref": "#/components/parameters/query_ad_account_id"
          }
        ],
        "responses": {
          "200": {
            "description": "response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Pin"
                }
              }
            }
          },
          "403": {
            "description": "Not authorized to access board or Pin.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                },
                "example": {
                  "code": 29,
                  "message": "You are not permitted to access that resource."
                }
              }
            }
          },
          "404": {
            "description": "Pin not found.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                },
                "example": {
                  "code": 50,
                  "message": "Pin not found."
                }
              }
            }
          },
          "default": {
            "description": "Unexpected error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      },
      "delete": {
        "summary": "Delete Pin",
        "description": "Delete a Pins owned by the \"operation user_account\" - or on a group board that has been shared with this account.\n- By default, the \"operation user_account\" is the token user_account.",
        "tags": [
          "pins"
        ],
        "operationId": "pins/delete",
        "security": [
          {
            "pinterest_oauth2": [
              "boards:read",
              "boards:write",
              "pins:read",
              "pins:write"
            ]
          }
        ],
        "x-ratelimit-category": "org_write",
        "x-sandbox": {
          "enabled": false
        },
        "parameters": [
          {
            "$ref": "#/components/parameters/path_pin_id"
          }
        ],
        "responses": {
          "204": {
            "description": "Successfully deleted Pin"
          },
          "403": {
            "description": "Not authorized to access board or Pin.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                },
                "example": {
                  "code": 403,
                  "message": "Not authorized to access board or Pin."
                }
              }
            }
          },
          "404": {
            "description": "Pin not found.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                },
                "example": {
                  "code": 404,
                  "message": "Pin not found."
                }
              }
            }
          },
          "default": {
            "description": "Unexpected error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      }
    },
    "/pins/{pin_id}/save": {
      "post": {
        "summary": "Save pin",
        "description": "Save a pin on a board or board section owned by the \"operation user_account\".\n- By default, the \"operation user_account\" is the token user_account.",
        "tags": [
          "pins"
        ],
        "operationId": "pins/save",
        "security": [
          {
            "pinterest_oauth2": [
              "boards:read",
              "boards:write",
              "pins:read",
              "pins:write"
            ]
          }
        ],
        "x-ratelimit-category": "org_write",
        "x-sandbox": {
          "enabled": false
        },
        "parameters": [
          {
            "$ref": "#/components/parameters/path_pin_id"
          }
        ],
        "requestBody": {
          "description": "Request object used to save an existing pin",
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "board_id": {
                    "description": "Unique identifier of the board to which the pin will be saved.",
                    "type": "string",
                    "pattern": "^\\d+$"
                  },
                  "board_section_id": {
                    "description": "Unique identifier of the board section to which the pin will be saved.",
                    "type": "string",
                    "pattern": "^\\d+$",
                    "nullable": true
                  }
                }
              }
            }
          }
        },
        "responses": {
          "201": {
            "description": "Successfully saved pin.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Pin"
                }
              }
            }
          },
          "403": {
            "description": "Not authorized to access Board or Pin.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                },
                "example": {
                  "code": 29,
                  "message": "You are not permitted to access that resource."
                }
              }
            }
          },
          "404": {
            "description": "Board or Pin not found.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                },
                "examples": {
                  "BoardNotFound": {
                    "value": {
                      "code": 40,
                      "message": "Board not found."
                    }
                  },
                  "PinNotFound": {
                    "value": {
                      "code": 50,
                      "message": "Pin not found."
                    }
                  },
                  "BoardSectionNotFound": {
                    "value": {
                      "code": 2031,
                      "message": "Sorry! We couldn't find this board section."
                    }
                  }
                }
              }
            }
          },
          "default": {
            "description": "Unexpected error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      }
    },
    "/pins/{pin_id}/analytics": {
      "get": {
        "summary": "Get Pin analytics",
