      "Pin": {
        "title": "Pin",
        "description": "Pin",
        "type": "object",
        "properties": {
          "id": {
            "type": "string",
            "readOnly": true,
            "example": "813744226420795884"
          },
          "created_at": {
            "type": "string",
            "format": "date-time",
            "readOnly": true,
            "example": "2020-01-01T20:10:40-00:00"
          },
          "link": {
            "type": "string",
            "nullable": true,
            "example": "https://www.pinterest.com/"
          },
          "title": {
            "type": "string",
            "nullable": true
          },
          "description": {
            "type": "string",
            "nullable": true
          },
          "dominant_color": {
            "type": "string",
            "nullable": true,
            "description": "Dominant pin color. Hex number, e.g. \\\"#6E7874\\\".",
            "example": "#6E7874"
          },
          "alt_text": {
            "type": "string",
            "nullable": true
          },
          "board_id": {
            "description": "The board to which this Pin belongs.",
            "type": "string",
            "pattern": "^\\d+$"
          },
          "board_section_id": {
            "description": "The board section to which this Pin belongs.",
            "type": "string",
            "pattern": "^\\d+$",
            "nullable": true
          },
          "board_owner": {
            "allOf": [
              {
                "$ref": "#/components/schemas/BoardOwner"
              }
            ],
            "type": "object",
            "readOnly": true
          },
          "media": {
            "allOf": [
              {
                "$ref": "#/components/schemas/PinMedia"
              }
            ],
            "type": "object",
            "readOnly": true
          },
          "media_source": {
            "allOf": [
              {
                "$ref": "#/components/schemas/PinMediaSource"
              }
            ],
            "type": "object",
            "writeOnly": true
          },
          "parent_pin_id": {
            "description": "The source pin id if this pin was saved from another pin. <a href=\"https://help.pinterest.com/article/save-pins-on-pinterest\">Learn more</a>.",
            "type": "string",
            "pattern": "^\\d+$",
            "nullable": true
          }
        }
      },
