    "/v2/aggs/ticker/{cryptoTicker}/range/{multiplier}/{timespan}/{from}/{to}": {
      "get": {
        "description": "Get aggregate bars for a cryptocurrency pair over a given date range in custom time window sizes.\n<br />\n<br />\nFor example, if timespan = \u2018minute\u2019 and multiplier = \u20185\u2019 then 5-minute bars will be returned.\n",
        "parameters": [
          {
            "description": "The ticker symbol of the currency pair.",
            "example": "X:BTCUSD",
            "in": "path",
            "name": "cryptoTicker",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "description": "The size of the timespan multiplier.",
            "example": 1,
            "in": "path",
            "name": "multiplier",
            "required": true,
            "schema": {
              "type": "integer"
            }
          },
          {
            "description": "The size of the time window.",
            "example": "day",
            "in": "path",
            "name": "timespan",
            "required": true,
            "schema": {
              "enum": [
                "minute",
                "hour",
                "day",
                "week",
                "month",
                "quarter",
                "year"
              ],
              "type": "string"
            }
          },
          {
            "description": "The start of the aggregate time window. Either a date with the format YYYY-MM-DD or a millisecond timestamp.",
            "example": "2021-07-22",
            "in": "path",
            "name": "from",
