{
  "components": {
    "schemas": {
      "AliasOperations": {
        "anyOf": [
          {
            "$ref": "#/components/schemas/CreateAliasOperation"
          },
          {
            "$ref": "#/components/schemas/DeleteAliasOperation"
          },
          {
            "$ref": "#/components/schemas/RenameAliasOperation"
          }
        ],
        "description": "Group of all the possible operations related to collection aliases"
      },
      "Batch": {
        "properties": {
          "ids": {
            "items": {
              "$ref": "#/components/schemas/ExtendedPointId"
            },
            "type": "array"
          },
          "payloads": {
            "items": {
              "anyOf": [
                {
                  "$ref": "#/components/schemas/Payload"
                },
                {
                  "nullable": true
                }
              ]
            },
            "nullable": true,
            "type": "array"
          },
          "vectors": {
            "items": {
              "items": {
                "format": "float",
                "type": "number"
              },
              "type": "array"
            },
            "type": "array"
          }
        },
        "required": [
          "ids",
          "vectors"
        ],
        "type": "object"
      },
      "ChangeAliasesOperation": {
        "description": "Operation for performing changes of collection aliases. Alias changes are atomic, meaning that no collection modifications can happen between alias operations.",
        "properties": {
          "actions": {
            "items": {
              "$ref": "#/components/schemas/AliasOperations"
            },
            "type": "array"
          }
        },
        "required": [
          "actions"
        ],
        "type": "object"
      },
      "ClusterStatus": {
        "description": "Information about current cluster status and structure",
        "oneOf": [
          {
            "properties": {
              "status": {
                "enum": [
                  "ClusterStatusDisabled"
                ],
                "type": "string"
              }
            },
            "required": [
              "status"
            ],
            "type": "object"
          },
          {
            "description": "Description of enabled cluster",
            "properties": {
              "peer_id": {
                "description": "ID of this peer",
                "format": "uint64",
                "minimum": 0,
                "type": "integer"
              },
              "peers": {
                "additionalProperties": {
                  "$ref": "#/components/schemas/PeerInfo"
                },
                "description": "Peers composition of the cluster with main information",
                "type": "object"
              },
              "raft_info": {
                "$ref": "#/components/schemas/RaftInfo"
              },
              "status": {
                "enum": [
                  "enabled"
                ],
                "type": "string"
              }
            },
            "required": [
              "peer_id",
              "peers",
              "raft_info",
              "status"
            ],
            "type": "object"
          }
        ]
      },
      "CollectionConfig": {
        "properties": {
          "hnsw_config": {
            "$ref": "#/components/schemas/HnswConfig"
          },
          "optimizer_config": {
            "$ref": "#/components/schemas/OptimizersConfig"
          },
          "params": {
            "$ref": "#/components/schemas/CollectionParams"
          },
          "wal_config": {
            "$ref": "#/components/schemas/WalConfig"
          }
        },
        "required": [
          "hnsw_config",
          "optimizer_config",
          "params",
          "wal_config"
        ],
        "type": "object"
      },
      "CollectionDescription": {
        "properties": {
          "name": {
            "type": "string"
          }
        },
        "required": [
          "name"
        ],
        "type": "object"
      },
      "CollectionInfo": {
        "description": "Current statistics and configuration of the collection",
        "properties": {
          "config": {
            "$ref": "#/components/schemas/CollectionConfig"
          },
          "disk_data_size": {
            "description": "Disk space, used by collection",
            "format": "uint",
            "minimum": 0,
            "type": "integer"
          },
          "optimizer_status": {
            "$ref": "#/components/schemas/OptimizersStatus"
          },
          "payload_schema": {
            "additionalProperties": {
              "$ref": "#/components/schemas/PayloadIndexInfo"
            },
            "description": "Types of stored payload",
            "type": "object"
          },
          "ram_data_size": {
            "description": "RAM used by collection",
            "format": "uint",
            "minimum": 0,
            "type": "integer"
          },
          "segments_count": {
            "description": "Number of segments in collection",
            "format": "uint",
            "minimum": 0,
            "type": "integer"
          },
          "status": {
            "$ref": "#/components/schemas/CollectionStatus"
          },
          "vectors_count": {
            "description": "Number of vectors in collection",
            "format": "uint",
            "minimum": 0,
            "type": "integer"
          }
        },
        "required": [
          "config",
          "disk_data_size",
          "optimizer_status",
          "payload_schema",
          "ram_data_size",
          "segments_count",
          "status",
          "vectors_count"
        ],
        "type": "object"
      },
      "CollectionParams": {
        "properties": {
          "distance": {
            "$ref": "#/components/schemas/Distance"
          },
          "shard_number": {
            "default": 1,
            "description": "Number of shards the collection has",
            "format": "uint32",
            "minimum": 1,
            "type": "integer"
          },
          "vector_size": {
            "description": "Size of a vectors used",
            "format": "uint",
            "minimum": 0,
            "type": "integer"
          }
        },
        "required": [
          "distance",
          "vector_size"
        ],
        "type": "object"
      },
      "CollectionStatus": {
        "description": "Current state of the collection",
        "enum": [
          "green",
          "yellow",
          "red"
        ],
        "type": "string"
      },
      "CollectionsResponse": {
        "properties": {
          "collections": {
            "items": {
              "$ref": "#/components/schemas/CollectionDescription"
            },
            "type": "array"
          }
        },
        "required": [
          "collections"
        ],
        "type": "object"
      },
      "Condition": {
        "anyOf": [
          {
            "$ref": "#/components/schemas/FieldCondition"
          },
          {
            "$ref": "#/components/schemas/IsEmptyCondition"
          },
          {
            "$ref": "#/components/schemas/HasIdCondition"
          },
          {
            "$ref": "#/components/schemas/Filter"
          }
        ]
      },
      "CreateAlias": {
        "description": "Create alternative name for a collection. Collection will be available under both names for search, retrieve,",
        "properties": {
          "alias_name": {
            "type": "string"
          },
          "collection_name": {
            "type": "string"
          }
        },
        "required": [
          "alias_name",
          "collection_name"
        ],
        "type": "object"
      },
      "CreateAliasOperation": {
        "properties": {
          "create_alias": {
            "$ref": "#/components/schemas/CreateAlias"
          }
        },
        "required": [
          "create_alias"
        ],
        "type": "object"
      },
      "CreateCollection": {
        "description": "Operation for creating new collection and (optionally) specify index params",
        "properties": {
          "distance": {
            "$ref": "#/components/schemas/Distance"
          },
          "hnsw_config": {
            "anyOf": [
              {
                "$ref": "#/components/schemas/HnswConfigDiff"
              },
              {
                "nullable": true
              }
            ],
            "description": "Custom params for HNSW index. If none - values from service configuration file are used."
          },
          "optimizers_config": {
            "anyOf": [
              {
                "$ref": "#/components/schemas/OptimizersConfigDiff"
              },
              {
                "nullable": true
              }
            ],
            "description": "Custom params for Optimizers.  If none - values from service configuration file are used."
          },
          "shard_number": {
            "default": 1,
            "description": "Number of shards in collection. Default is 1, minimum is 1.",
            "format": "uint32",
            "minimum": 0,
            "type": "integer"
          },
          "vector_size": {
            "format": "uint",
            "minimum": 0,
            "type": "integer"
          },
          "wal_config": {
            "anyOf": [
              {
                "$ref": "#/components/schemas/WalConfigDiff"
              },
              {
                "nullable": true
              }
            ],
            "description": "Custom params for WAL. If none - values from service configuration file are used."
          }
        },
        "required": [
          "distance",
          "vector_size"
        ],
        "type": "object"
      },
      "CreateFieldIndex": {
        "properties": {
          "field_name": {
            "type": "string"
          },
          "field_type": {
            "anyOf": [
              {
                "$ref": "#/components/schemas/PayloadSchemaType"
              },
              {
                "nullable": true
              }
            ]
          }
        },
        "required": [
          "field_name"
        ],
        "type": "object"
      },
      "DeleteAlias": {
        "description": "Delete alias if exists",
        "properties": {
          "alias_name": {
            "type": "string"
          }
        },
        "required": [
          "alias_name"
        ],
        "type": "object"
      },
      "DeleteAliasOperation": {
        "description": "Delete alias if exists",
        "properties": {
          "delete_alias": {
            "$ref": "#/components/schemas/DeleteAlias"
          }
        },
        "required": [
          "delete_alias"
        ],
        "type": "object"
      },
      "DeletePayload": {
        "properties": {
          "keys": {
            "items": {
              "type": "string"
            },
            "type": "array"
          },
          "points": {
            "description": "Deletes values from each point in this list",
            "items": {
              "$ref": "#/components/schemas/ExtendedPointId"
            },
            "type": "array"
          }
        },
        "required": [
          "keys",
          "points"
        ],
        "type": "object"
      },
      "Distance": {
        "description": "Type of internal tags, build from payload Distance function types used to compare vectors",
        "enum": [
          "Cosine",
          "Euclid",
          "Dot"
        ],
        "type": "string"
      },
      "ErrorResponse": {
        "properties": {
          "result": {
            "nullable": true,
            "type": "object"
          },
          "status": {
            "properties": {
              "error": {
                "description": "Description of the occurred error.",
                "type": "string"
              }
            },
            "type": "object"
          },
          "time": {
            "description": "Time spent to process this request",
            "format": "float",
            "type": "number"
          }
        },
        "type": "object"
      },
      "ExtendedPointId": {
        "anyOf": [
          {
            "format": "uint64",
            "minimum": 0,
            "type": "integer"
          },
          {
            "format": "uuid",
            "type": "string"
          }
        ],
        "description": "Type, used for specifying point ID in user interface"
      },
      "FieldCondition": {
        "description": "All possible payload filtering conditions",
        "properties": {
          "geo_bounding_box": {
            "anyOf": [
              {
                "$ref": "#/components/schemas/GeoBoundingBox"
              },
              {
                "nullable": true
              }
            ],
            "description": "Check if points geo location lies in a given area"
          },
          "geo_radius": {
            "anyOf": [
              {
                "$ref": "#/components/schemas/GeoRadius"
              },
              {
                "nullable": true
              }
            ],
            "description": "Check if geo point is within a given radius"
          },
          "key": {
            "description": "Payload key",
            "type": "string"
          },
          "match": {
            "anyOf": [
              {
                "$ref": "#/components/schemas/Match"
              },
              {
                "nullable": true
              }
            ],
            "description": "Check if point has field with a given value"
          },
          "range": {
            "anyOf": [
              {
                "$ref": "#/components/schemas/Range"
              },
              {
                "nullable": true
              }
            ],
            "description": "Check if points value lies in a given range"
          },
          "values_count": {
            "anyOf": [
              {
                "$ref": "#/components/schemas/ValuesCount"
              },
              {
                "nullable": true
              }
            ],
            "description": "Check number of values of the field"
          }
        },
        "required": [
          "key"
        ],
        "type": "object"
      },
      "Filter": {
        "additionalProperties": false,
        "properties": {
          "must": {
            "description": "All conditions must match",
            "items": {
              "$ref": "#/components/schemas/Condition"
            },
            "nullable": true,
            "type": "array"
          },
          "must_not": {
            "description": "All conditions must NOT match",
            "items": {
              "$ref": "#/components/schemas/Condition"
            },
            "nullable": true,
            "type": "array"
          },
          "should": {
            "description": "At least one of those conditions should match",
            "items": {
              "$ref": "#/components/schemas/Condition"
            },
            "nullable": true,
            "type": "array"
          }
        },
        "type": "object"
      },
      "FilterSelector": {
        "properties": {
          "filter": {
            "$ref": "#/components/schemas/Filter"
          }
        },
        "required": [
          "filter"
        ],
        "type": "object"
      },
      "GeoBoundingBox": {
        "description": "Geo filter request\n\nMatches coordinates inside the rectangle, described by coordinates of lop-left and bottom-right edges",
        "properties": {
          "bottom_right": {
            "$ref": "#/components/schemas/GeoPoint"
          },
          "top_left": {
            "$ref": "#/components/schemas/GeoPoint"
          }
        },
        "required": [
          "bottom_right",
          "top_left"
        ],
        "type": "object"
      },
      "GeoPoint": {
        "description": "Geo point payload schema",
        "properties": {
          "lat": {
            "format": "double",
            "type": "number"
          },
          "lon": {
            "format": "double",
            "type": "number"
          }
        },
        "required": [
          "lat",
          "lon"
        ],
        "type": "object"
      },
      "GeoRadius": {
        "description": "Geo filter request\n\nMatches coordinates inside the circle of `radius` and center with coordinates `center`",
        "properties": {
          "center": {
            "$ref": "#/components/schemas/GeoPoint"
          },
          "radius": {
            "description": "Radius of the area in meters",
            "format": "double",
            "type": "number"
          }
        },
        "required": [
          "center",
          "radius"
        ],
        "type": "object"
      },
      "HasIdCondition": {
        "description": "ID-based filtering condition",
        "properties": {
          "has_id": {
            "items": {
              "$ref": "#/components/schemas/ExtendedPointId"
            },
            "type": "array",
            "uniqueItems": true
          }
        },
        "required": [
          "has_id"
        ],
        "type": "object"
      },
      "HnswConfig": {
        "description": "Config of HNSW index",
        "properties": {
          "ef_construct": {
            "description": "Number of neighbours to consider during the index building. Larger the value - more accurate the search, more time required to build index.",
            "format": "uint",
            "minimum": 0,
            "type": "integer"
          },
          "full_scan_threshold": {
            "description": "Minimal amount of points for additional payload-based indexing. If payload chunk is smaller than `full_scan_threshold` additional indexing won't be used - in this case full-scan search should be preferred by query planner and additional indexing is not required.",
            "format": "uint",
            "minimum": 0,
            "type": "integer"
          },
          "m": {
            "description": "Number of edges per node in the index graph. Larger the value - more accurate the search, more space required.",
            "format": "uint",
            "minimum": 0,
            "type": "integer"
          }
        },
        "required": [
          "ef_construct",
          "full_scan_threshold",
          "m"
        ],
        "type": "object"
      },
      "HnswConfigDiff": {
        "properties": {
          "ef_construct": {
            "description": "Number of neighbours to consider during the index building. Larger the value - more accurate the search, more time required to build index.",
            "format": "uint",
            "minimum": 0,
            "nullable": true,
            "type": "integer"
          },
          "full_scan_threshold": {
            "description": "Minimal amount of points for additional payload-based indexing. If payload chunk is smaller than `full_scan_threshold` additional indexing won't be used - in this case full-scan search should be preferred by query planner and additional indexing is not required.",
            "format": "uint",
            "minimum": 0,
            "nullable": true,
            "type": "integer"
          },
          "m": {
            "description": "Number of edges per node in the index graph. Larger the value - more accurate the search, more space required.",
            "format": "uint",
            "minimum": 0,
            "nullable": true,
            "type": "integer"
          }
        },
        "type": "object"
      },
      "IsEmptyCondition": {
        "description": "Select points with empty payload for a specified field",
        "properties": {
          "is_empty": {
            "$ref": "#/components/schemas/PayloadField"
          }
        },
        "required": [
          "is_empty"
        ],
        "type": "object"
      },
      "Match": {
        "anyOf": [
          {
            "$ref": "#/components/schemas/MatchValue"
          },
          {
            "$ref": "#/components/schemas/MatchKeyword"
          },
          {
            "$ref": "#/components/schemas/MatchInteger"
          }
        ],
        "description": "Match filter request"
      },
      "MatchInteger": {
        "deprecated": true,
        "description": "Match filter request (deprecated)",
        "properties": {
          "integer": {
            "deprecated": true,
            "description": "Integer value to match",
            "format": "int64",
            "type": "integer"
          }
        },
        "required": [
          "integer"
        ],
        "type": "object"
      },
      "MatchKeyword": {
        "deprecated": true,
        "description": "Match by keyword (deprecated)",
        "properties": {
          "keyword": {
            "deprecated": true,
            "description": "Keyword value to match",
            "type": "string"
          }
        },
        "required": [
          "keyword"
        ],
        "type": "object"
      },
      "MatchValue": {
        "properties": {
          "value": {
            "$ref": "#/components/schemas/ValueVariants"
          }
        },
        "required": [
          "value"
        ],
        "type": "object"
      },
      "OptimizersConfig": {
        "properties": {
          "default_segment_number": {
            "description": "Target amount of segments optimizer will try to keep. Real amount of segments may vary depending on multiple parameters: - Amount of stored points - Current write RPS\n\nIt is recommended to select default number of segments as a factor of the number of search threads, so that each segment would be handled evenly by one of the threads",
            "format": "uint",
            "minimum": 0,
            "type": "integer"
          },
          "deleted_threshold": {
            "description": "The minimal fraction of deleted vectors in a segment, required to perform segment optimization",
            "format": "double",
            "type": "number"
          },
          "flush_interval_sec": {
            "description": "Minimum interval between forced flushes.",
            "format": "uint64",
            "minimum": 0,
            "type": "integer"
          },
          "indexing_threshold": {
            "description": "Maximum number of vectors allowed for plain index. Default value based on https://github.com/google-research/google-research/blob/master/scann/docs/algorithms.md",
            "format": "uint",
            "minimum": 0,
            "type": "integer"
          },
          "max_optimization_threads": {
            "description": "Maximum available threads for optimization workers",
            "format": "uint",
            "minimum": 0,
            "type": "integer"
          },
          "max_segment_size": {
            "description": "Do not create segments larger this number of points. Large segments might require disproportionately long indexation times, therefore it makes sense to limit the size of segments.\n\nIf indexation speed have more priority for your - make this parameter lower. If search speed is more important - make this parameter higher.",
            "format": "uint",
            "minimum": 0,
            "type": "integer"
          },
          "memmap_threshold": {
            "description": "Maximum number of vectors to store in-memory per segment. Segments larger than this threshold will be stored as read-only memmaped file.",
            "format": "uint",
            "minimum": 0,
            "type": "integer"
          },
          "payload_indexing_threshold": {
            "description": "Starting from this amount of vectors per-segment the engine will start building index for payload.",
            "format": "uint",
            "minimum": 0,
            "type": "integer"
          },
          "vacuum_min_vector_number": {
            "description": "The minimal number of vectors in a segment, required to perform segment optimization",
            "format": "uint",
            "minimum": 0,
            "type": "integer"
          }
        },
        "required": [
          "default_segment_number",
          "deleted_threshold",
          "flush_interval_sec",
          "indexing_threshold",
          "max_optimization_threads",
          "max_segment_size",
          "memmap_threshold",
          "payload_indexing_threshold",
          "vacuum_min_vector_number"
        ],
        "type": "object"
      },
      "OptimizersConfigDiff": {
        "properties": {
          "default_segment_number": {
            "description": "Target amount of segments optimizer will try to keep. Real amount of segments may vary depending on multiple parameters: - Amount of stored points - Current write RPS\n\nIt is recommended to select default number of segments as a factor of the number of search threads, so that each segment would be handled evenly by one of the threads",
            "format": "uint",
            "minimum": 0,
            "nullable": true,
            "type": "integer"
          },
          "deleted_threshold": {
            "description": "The minimal fraction of deleted vectors in a segment, required to perform segment optimization",
            "format": "double",
            "nullable": true,
            "type": "number"
          },
          "flush_interval_sec": {
            "description": "Minimum interval between forced flushes.",
            "format": "uint64",
            "minimum": 0,
            "nullable": true,
            "type": "integer"
          },
          "indexing_threshold": {
            "description": "Maximum number of vectors allowed for plain index. Default value based on https://github.com/google-research/google-research/blob/master/scann/docs/algorithms.md",
            "format": "uint",
            "minimum": 0,
            "nullable": true,
            "type": "integer"
          },
          "max_optimization_threads": {
            "description": "Maximum available threads for optimization workers",
            "format": "uint",
            "minimum": 0,
            "nullable": true,
            "type": "integer"
          },
          "max_segment_size": {
            "description": "Do not create segments larger this number of points. Large segments might require disproportionately long indexation times, therefore it makes sense to limit the size of segments.\n\nIf indexation speed have more priority for your - make this parameter lower. If search speed is more important - make this parameter higher.",
            "format": "uint",
            "minimum": 0,
            "nullable": true,
            "type": "integer"
          },
          "memmap_threshold": {
            "description": "Maximum number of vectors to store in-memory per segment. Segments larger than this threshold will be stored as read-only memmaped file.",
            "format": "uint",
            "minimum": 0,
            "nullable": true,
            "type": "integer"
          },
          "payload_indexing_threshold": {
            "description": "Starting from this amount of vectors per-segment the engine will start building index for payload.",
            "format": "uint",
            "minimum": 0,
            "nullable": true,
            "type": "integer"
          },
          "vacuum_min_vector_number": {
            "description": "The minimal number of vectors in a segment, required to perform segment optimization",
            "format": "uint",
            "minimum": 0,
            "nullable": true,
            "type": "integer"
          }
        },
        "type": "object"
      },
      "OptimizersStatus": {
        "description": "Current state of the collection",
        "oneOf": [
          {
            "enum": [
              "ok"
            ],
            "type": "string"
          },
          {
            "additionalProperties": false,
            "description": "Something wrong happened with optimizers",
            "properties": {
              "error": {
                "type": "string"
              }
            },
            "required": [
              "error"
            ],
            "type": "object"
          }
        ]
      },
      "Payload": {
        "additionalProperties": true,
        "type": "object"
      },
      "PayloadField": {
        "description": "Payload field",
        "properties": {
          "key": {
            "description": "Payload field name",
            "type": "string"
          }
        },
        "required": [
          "key"
        ],
        "type": "object"
      },
      "PayloadIndexInfo": {
        "description": "Payload field type & index information",
        "properties": {
          "data_type": {
