            "schema": {
              "type": "string",
              "default": "test.caller"
            }
          },
          {
            "name": "X-Track-Session-Id",
            "in": "header",
            "required": true,
            "description": "Track information: track.session_id",
            "schema": {
              "type": "string",
              "default": "test.session_id"
            }
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "multipart/form-data": {
              "schema": {
                "type": "object",
                "required": [
                  "id",
                  "user",
                  "attachment",
                  "object"
                ],
                "properties": {
                  "id": {
                    "type": "string",
                    "description": "id"
                  },
                  "user": {
                    "type": "string",
                    "description": "user"
                  },
                  "attachment": {
                    "type": "string",
                    "format": "binary",
                    "description": "attachment"
                  },
                  "object": {
                    "$ref": "#/components/schemas/Something",
                    "description": "object"
                  }
                }
              },
              "encoding": {
                "id": {
                  "contentType": "text/plain"
                },
                "user": {
                  "contentType": "text/plain"
                },
                "attachment": {
                  "contentType": "application/octect-stream"
                },
                "object": {
                  "contentType": "application/json"
                }
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "list of created Something objects",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/Something"
                  }
                }
              }
            }
          },
          "400": {
            "description": "Missing or invalid fields",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "required": [
                    "upload_something"
                  ],
                  "properties": {
                    "upload_something": {
                      "type": "string"
                    }
                  },
                  "description": "upload_something string payload"
                }
              }
            }
          }
        },
        "tags": [
          "simple_example.0x14"
        ],
        "security": [
          {
            "auth.bearer": []
          }
        ]
      }
    },
