{
    "info": {
        "description": "Documentation for the Unmanic application API",
        "title": "Unmanic API",
        "version": "2"
    },
    "servers": [
        {
            "url": "http://localhost:8888/unmanic/api/v2/",
            "description": "Local environment"
        }
    ],
    "components": {
        "securitySchemes": {
            "BasicAuth": {
                "type": "http",
                "scheme": "basic"
            }
        },
        "schemas": {
            "DocumentContentSuccess": {
                "type": "object",
                "properties": {
                    "content": {
                        "type": "array",
                        "minItems": 1,
                        "description": "Document contents read line-by-line into a list",
                        "example": [
                            "First line\n",
                            "Second line\n",
                            "\n"
                        ],
                        "items": {
                            "type": "string"
                        }
                    }
                },
                "required": [
                    "content"
                ]
            },
            "BadRequest": {
                "type": "object",
                "properties": {
                    "error": {
                        "type": "string",
                        "description": "Return status code and reason",
                        "example": "400: Failed request schema validation"
                    },
                    "messages": {
                        "type": "object",
                        "description": "Attached request body validation errors",
                        "example": {
                            "name": [
                                "The thing that went wrong."
                            ]
                        }
                    },
                    "traceback": {
                        "type": "array",
                        "description": "Attached exception traceback (if developer mode is enabled)",
                        "example": [
                            "Traceback (most recent call last):\n",
                            "...",
                            "json.decoder.JSONDecodeError: Expecting value: line 3 column 14 (char 45)\n"
                        ],
                        "items": {
                            "type": "string"
                        }
                    }
                },
                "required": [
                    "error",
                    "messages"
                ]
            },
            "BadEndpoint": {
                "type": "object",
                "properties": {
                    "error": {
                        "type": "string",
                        "description": "Return status code and reason",
                        "example": "404: Endpoint not found"
                    }
                },
                "required": [
                    "error"
                ]
            },
            "BadMethod": {
                "type": "object",
                "properties": {
                    "error": {
                        "type": "string",
                        "description": "Return status code and reason",
                        "example": "405: Method 'GET' not allowed"
                    }
                },
                "required": [
                    "error"
                ]
            },
            "InternalError": {
                "type": "object",
                "properties": {
                    "error": {
                        "type": "string",
                        "description": "Return status code and reason",
                        "example": "500: Caught exception message"
                    },
                    "messages": {
                        "type": "object",
                        "description": "Attached request body validation errors",
                        "example": {
                            "name": [
                                "The thing that went wrong."
                            ]
                        }
                    },
                    "traceback": {
                        "type": "array",
                        "description": "Attached exception traceback (if developer mode is enabled)",
                        "example": [
                            "Traceback (most recent call last):\n",
                            "...",
                            "json.decoder.JSONDecodeError: Expecting value: line 3 column 14 (char 45)\n"
                        ],
                        "items": {
                            "type": "string"
                        }
                    }
                },
                "required": [
                    "error",
                    "messages"
                ]
            },
            "RequestDirectoryListingData": {
                "type": "object",
                "properties": {
                    "current_path": {
                        "type": "string",
                        "default": "/",
                        "example": "/"
                    },
                    "list_type": {
                        "type": "string",
                        "default": "all",
                        "example": "directories"
                    }
                }
            },
            "DirectoryListingResults": {
                "type": "object",
                "properties": {
                    "directories": {
                        "type": "array",
                        "minItems": 0,
                        "description": "A list of directories in the given path",
                        "example": [
                            {
                                "value": "home",
                                "label": "/home"
                            },
                            {
                                "value": "tmp",
                                "label": "/tmp"
                            }
                        ],
                        "items": {
                            "type": "object"
                        }
                    },
                    "files": {
                        "type": "array",
                        "minItems": 0,
                        "description": "A list of files in the given path",
                        "example": [
                            {
                                "value": "file1.txt",
                                "label": "/file1.txt"
                            },
                            {
                                "value": "file2.txt",
                                "label": "/file2.txt"
                            }
                        ],
                        "items": {
                            "type": "object"
                        }
                    }
                },
                "required": [
                    "directories",
                    "files"
                ]
            },
            "RequestHistoryTableData": {
                "type": "object",
                "properties": {
                    "start": {
                        "type": "integer",
                        "default": 0,
                        "description": "Start row number to select from",
                        "example": 0
                    },
                    "length": {
                        "type": "integer",
                        "default": 10,
                        "description": "Number of rows to select",
                        "example": 10
                    },
                    "search_value": {
                        "type": "string",
                        "default": "",
                        "description": "String to filter search results by",
                        "example": "items with this text in the value"
                    },
                    "order_by": {
                        "type": "string",
                        "default": "finish_time",
                        "example": "finish_time"
                    },
                    "order_direction": {
                        "type": "string",
                        "enum": [
                            "asc",
                            "desc"
                        ],
                        "description": "Order direction ('asc' or 'desc')",
                        "example": "desc"
                    }
                }
            },
            "CompletedTasksTableResults": {
                "type": "object",
                "properties": {
                    "id": {
                        "type": "integer",
                        "description": "Item ID",
                        "example": 1
                    },
                    "task_label": {
                        "type": "string",
                        "description": "Item label",
                        "example": "example.mp4"
                    },
                    "task_success": {
                        "type": "boolean",
                        "description": "Item success status",
                        "example": true
                    },
                    "finish_time": {
                        "type": "integer",
                        "description": "Item finish time",
                        "example": 1627392616.6400812
                    }
                },
                "required": [
                    "finish_time",
                    "id",
                    "task_label",
                    "task_success"
                ]
            },
            "CompletedTasks": {
                "type": "object",
                "properties": {
                    "recordsTotal": {
                        "type": "integer",
                        "description": "Total number of records in this table",
                        "example": 329
                    },
                    "recordsFiltered": {
                        "type": "integer",
                        "default": 10,
                        "description": "Total number of records after filters have been applied",
                        "example": 10
                    },
                    "results": {
                        "minItems": 0,
                        "description": "Results",
                        "type": "array",
                        "items": {
                            "$ref": "#/components/schemas/CompletedTasksTableResults"
                        }
                    },
                    "successCount": {
                        "type": "integer",
                        "description": "Total count of times with a success status in the results list",
                        "example": 337
                    },
                    "failedCount": {
                        "type": "integer",
                        "description": "Total count of times with a failed status in the results list",
                        "example": 2
                    }
                },
                "required": [
                    "failedCount",
                    "results",
                    "successCount"
                ]
            },
            "RequestTableUpdateByIdList": {
                "type": "object",
                "properties": {
                    "id_list": {
                        "type": "array",
                        "minItems": 1,
                        "description": "List of table IDs",
                        "example": [],
                        "items": {
                            "type": "integer"
                        }
                    }
                },
                "required": [
                    "id_list"
                ]
            },
            "BaseSuccess": {
                "type": "object",
                "properties": {
                    "success": {
                        "type": "boolean",
                        "description": "This is always \"True\" when a request succeeds",
                        "example": true
                    }
                },
                "required": [
                    "success"
                ]
            },
            "RequestAddCompletedToPendingTasks": {
                "type": "object",
                "properties": {
                    "id_list": {
                        "type": "array",
                        "minItems": 1,
                        "description": "List of table IDs",
                        "example": [],
                        "items": {
                            "type": "integer"
                        }
                    },
                    "library_id": {
                        "type": "integer",
                        "default": 0,
                        "example": 1
                    }
                },
                "required": [
                    "id_list"
                ]
            },
            "CompletedTasksLogRequest": {
                "type": "object",
                "properties": {
                    "task_id": {
                        "type": "integer",
                        "description": "The ID of the task",
                        "example": 1
                    }
                },
                "required": [
                    "task_id"
                ]
            },
            "CompletedTasksLog": {
                "type": "object",
                "properties": {
                    "command_log": {
                        "type": "string",
                        "description": "Long string...",
                        "example": "Long string..."
                    },
                    "command_log_lines": {
                        "type": "array",
                        "description": "The long string broken up into an array of lines",
                        "example": [
                            "",
                            "<b>RUNNER: </b>",
                            "Video Encoder H264 - libx264 [Pass #1]",
                            "",
                            "<b>COMMAND:</b>",
                            "",
                            "..."
                        ],
                        "items": {
                            "type": "string"
                        }
                    }
                },
                "required": [
                    "command_log",
                    "command_log_lines"
                ]
            },
            "RequestPendingTableData": {
                "type": "object",
                "properties": {
                    "start": {
                        "type": "integer",
                        "default": 0,
                        "description": "Start row number to select from",
                        "example": 0
                    },
                    "length": {
                        "type": "integer",
                        "default": 10,
                        "description": "Number of rows to select",
                        "example": 10
                    },
                    "search_value": {
                        "type": "string",
                        "default": "",
                        "description": "String to filter search results by",
                        "example": "items with this text in the value"
                    },
                    "order_by": {
                        "type": "string",
                        "default": "priority",
                        "example": "priority"
                    },
                    "order_direction": {
                        "type": "string",
                        "enum": [
                            "asc",
                            "desc"
                        ],
                        "description": "Order direction ('asc' or 'desc')",
                        "example": "desc"
                    }
                }
            },
            "PendingTasksTableResults": {
                "type": "object",
                "properties": {
                    "id": {
                        "type": "integer",
                        "description": "Item ID",
                        "example": 1
                    },
                    "abspath": {
                        "type": "string",
                        "description": "File absolute path",
                        "example": "example.mp4"
                    },
                    "priority": {
                        "type": "integer",
                        "description": "The current priority (higher is greater)",
                        "example": 100
                    },
                    "type": {
                        "type": "string",
                        "description": "The type of the pending task - local or remote",
                        "example": "local"
                    },
                    "status": {
                        "type": "string",
                        "description": "The current status of the pending task",
                        "example": "pending"
                    },
                    "checksum": {
                        "type": "string",
                        "description": "The uploaded file md5 checksum",
                        "example": "5425ab3df5cdbad2e1099bb4cb963a4f"
                    },
                    "library_id": {
                        "type": "integer",
                        "description": "The ID of the library for which this task was created",
                        "example": 1
                    },
                    "library_name": {
                        "type": "string",
                        "description": "The name of the library for which this task was created",
                        "example": "Default"
                    }
                },
                "required": [
                    "abspath",
                    "id",
                    "priority",
                    "status",
                    "type"
                ]
            },
            "PendingTasks": {
                "type": "object",
                "properties": {
                    "recordsTotal": {
                        "type": "integer",
                        "description": "Total number of records in this table",
                        "example": 329
                    },
                    "recordsFiltered": {
                        "type": "integer",
                        "default": 10,
                        "description": "Total number of records after filters have been applied",
                        "example": 10
                    },
                    "results": {
                        "minItems": 0,
                        "description": "Results",
                        "type": "array",
                        "items": {
                            "$ref": "#/components/schemas/PendingTasksTableResults"
                        }
                    }
                },
                "required": [
                    "results"
                ]
            },
            "RequestPendingTasksReorder": {
                "type": "object",
                "properties": {
                    "id_list": {
                        "type": "array",
                        "minItems": 1,
                        "description": "List of table IDs",
                        "example": [],
                        "items": {
                            "type": "integer"
                        }
                    },
                    "position": {
                        "type": "string",
                        "enum": [
                            "top",
                            "bottom"
                        ],
                        "description": "Position to move given list of items to ('top' or 'bottom')",
                        "example": "top"
                    }
                },
                "required": [
                    "id_list",
                    "position"
                ]
            },
            "RequestPendingTaskCreate": {
                "type": "object",
                "properties": {
                    "path": {
                        "type": "string",
                        "example": "/library/TEST_FILE.mkv"
                    },
                    "library_id": {
                        "type": "integer",
                        "example": 1
                    },
                    "library_name": {
                        "type": "string",
                        "example": "Default"
                    }
                },
                "required": [
                    "path"
                ]
            },
            "RequestPendingTasksLibraryUpdate": {
                "type": "object",
                "properties": {
                    "id_list": {
                        "type": "array",
                        "minItems": 1,
                        "description": "List of table IDs",
                        "example": [],
                        "items": {
                            "type": "integer"
                        }
                    },
                    "library_name": {
                        "type": "string",
                        "example": "Default"
                    }
                },
                "required": [
                    "id_list",
                    "library_name"
                ]
            },
            "TaskDownloadLink": {
                "type": "object",
                "properties": {
                    "link_id": {
                        "type": "string",
                        "description": "The ID used to download the file /unmanic/downloads/{link_id}",
                        "example": "2960645c-a4e2-4b05-8866-7bd469ee9ef8"
                    }
                },
                "required": [
                    "link_id"
                ]
            },
            "RequestPluginsTableData": {
                "type": "object",
                "properties": {
                    "start": {
                        "type": "integer",
                        "default": 0,
                        "description": "Start row number to select from",
                        "example": 0
                    },
                    "length": {
                        "type": "integer",
                        "default": 10,
                        "description": "Number of rows to select",
                        "example": 10
                    },
                    "search_value": {
                        "type": "string",
                        "default": "",
                        "description": "String to filter search results by",
                        "example": "items with this text in the value"
                    },
                    "order_by": {
                        "type": "string",
                        "default": "name",
                        "example": "name"
                    },
                    "order_direction": {
                        "type": "string",
                        "enum": [
                            "asc",
                            "desc"
                        ],
                        "description": "Order direction ('asc' or 'desc')",
                        "example": "desc"
                    }
                }
            },
            "PluginStatus": {
                "type": "object",
                "properties": {
                    "installed": {
                        "type": "boolean",
                        "description": "Is the plugin installed",
                        "example": true
                    },
                    "update_available": {
                        "type": "boolean",
                        "description": "Does the plugin have an update available",
                        "example": true
                    }
                }
            },
            "PluginsTableResults": {
                "type": "object",
                "properties": {
                    "plugin_id": {
                        "type": "string",
                        "description": "The plugin ID",
                        "example": "encoder_video_h264_nvenc"
                    },
                    "name": {
                        "type": "string",
                        "description": "The plugin name",
                        "example": "Video Encoder H264 - h264_nvenc"
                    },
                    "author": {
                        "type": "string",
                        "description": "The plugin author",
                        "example": "encoder_video_h264_nvenc"
                    },
                    "description": {
                        "type": "string",
                        "description": "The plugin description",
                        "example": "Ensure all video streams are encoded with the H264 codec using the h264_nvenc encoder."
                    },
                    "version": {
                        "type": "string",
                        "description": "The plugin version",
                        "example": "Josh.5"
                    },
                    "icon": {
                        "type": "string",
                        "description": "The plugin icon",
                        "example": "https://raw.githubusercontent.com/Josh5/unmanic-plugins/master/source/encoder_video_h264_nvenc/icon.png"
                    },
                    "tags": {
                        "type": "string",
                        "description": "The plugin tags",
                        "example": "video,encoder,ffmpeg,worker,nvenc,nvdec,nvidia"
                    },
                    "status": {
                        "description": "The plugin status",
                        "allOf": [
                            {
                                "$ref": "#/components/schemas/PluginStatus"
                            }
                        ]
                    },
                    "changelog": {
                        "type": "string",
                        "description": "The plugin changelog",
                        "example": "[b][color=56adda]0.0.1[/color][/b]• initial version"
                    },
                    "has_config": {
                        "type": "boolean",
                        "description": "The plugin has the ability to be configured",
                        "example": true
                    },
                    "id": {
                        "type": "integer",
                        "description": "Item table ID",
                        "example": 1
                    }
                },
                "required": [
                    "author",
                    "description",
                    "icon",
                    "id",
                    "name",
                    "plugin_id",
                    "status",
                    "tags",
                    "version"
                ]
            },
            "PluginsData": {
                "type": "object",
                "properties": {
                    "recordsTotal": {
                        "type": "integer",
                        "description": "Total number of records in this table",
                        "example": 329
                    },
                    "recordsFiltered": {
                        "type": "integer",
                        "default": 10,
                        "description": "Total number of records after filters have been applied",
                        "example": 10
                    },
                    "results": {
                        "minItems": 0,
                        "description": "Results",
                        "type": "array",
                        "items": {
                            "$ref": "#/components/schemas/PluginsTableResults"
                        }
                    }
                },
                "required": [
                    "results"
                ]
            },
            "RequestPluginsInfo": {
                "type": "object",
                "properties": {
                    "plugin_id": {
                        "type": "string",
                        "example": "encoder_video_hevc_vaapi"
                    },
                    "repo_id": {
                        "type": "string",
                        "description": "The ID of the repository that this plugin is in",
                        "example": "158899500680826593283708490873332175078"
                    },
                    "prefer_local": {
                        "type": "boolean",
                        "default": true,
                        "example": true
                    },
                    "library_id": {
                        "type": "integer",
                        "default": 0,
                        "example": 1
                    }
                },
                "required": [
                    "plugin_id"
                ]
            },
            "PluginsConfigInputItem": {
                "type": "object",
                "properties": {
                    "key_id": {
                        "type": "string",
                        "description": "The config input base64 encoded key (used for linking keys containing spaces, etc.)",
                        "example": "c8f122656ed2acabde9b57101a4c8ec7"
                    },
                    "key": {
                        "type": "string",
                        "description": "The config input key or name",
                        "example": "downmix_dts_hd_ma"
                    },
                    "value": {
                        "description": "The current value of this config input",
                        "example": false
                    },
                    "input_type": {
                        "type": "string",
                        "description": "The config input type",
                        "example": "checkbox"
                    },
                    "label": {
                        "type": "string",
                        "description": "The label used to define this config input",
                        "example": "Downmix DTS-HD Master Audio (max 5.1 channels)?"
                    },
                    "select_options": {
                        "type": "array",
                        "description": "Additional options if the input_type is set to 'select'",
                        "example": [
                            {
                                "value": "first",
                                "label": "First Option"
                            },
                            {
                                "value": "second",
                                "label": "Second Option"
                            }
                        ],
                        "items": {
                            "type": "object"
                        }
                    },
                    "slider_options": {
                        "type": "object",
                        "description": "Additional options if the input_type is set to 'slider'",
                        "example": {
                            "min": 1,
                            "max": 8,
                            "suffix": "M"
                        }
                    },
                    "display": {
                        "type": "string",
                        "description": "Should the setting input be displayed (visible, hidden)",
                        "example": "visible"
                    }
                },
                "required": [
                    "display",
                    "input_type",
                    "key",
                    "key_id",
                    "label",
                    "select_options",
                    "slider_options",
                    "value"
                ]
            },
            "PluginsInfoResults": {
                "type": "object",
                "properties": {
                    "plugin_id": {
                        "type": "string",
                        "description": "The plugin ID",
                        "example": "encoder_video_h264_nvenc"
                    },
                    "name": {
                        "type": "string",
                        "description": "The plugin name",
                        "example": "Video Encoder H264 - h264_nvenc"
                    },
                    "author": {
                        "type": "string",
                        "description": "The plugin author",
                        "example": "encoder_video_h264_nvenc"
                    },
                    "description": {
                        "type": "string",
                        "description": "The plugin description",
                        "example": "Ensure all video streams are encoded with the H264 codec using the h264_nvenc encoder."
                    },
                    "version": {
                        "type": "string",
                        "description": "The plugin version",
                        "example": "Josh.5"
                    },
                    "icon": {
                        "type": "string",
                        "description": "The plugin icon",
                        "example": "https://raw.githubusercontent.com/Josh5/unmanic-plugins/master/source/encoder_video_h264_nvenc/icon.png"
                    },
                    "tags": {
                        "type": "string",
                        "description": "The plugin tags",
                        "example": "video,encoder,ffmpeg,worker,nvenc,nvdec,nvidia"
                    },
                    "status": {
                        "description": "The plugin status",
                        "allOf": [
                            {
                                "$ref": "#/components/schemas/PluginStatus"
                            }
                        ]
                    },
                    "changelog": {
                        "type": "string",
                        "description": "The plugin changelog",
                        "example": "[b][color=56adda]0.0.1[/color][/b]• initial version"
                    },
                    "has_config": {
                        "type": "boolean",
                        "description": "The plugin has the ability to be configured",
                        "example": true
                    },
                    "settings": {
                        "description": "The plugin settings",
                        "type": "array",
                        "items": {
                            "$ref": "#/components/schemas/PluginsConfigInputItem"
                        }
                    }
                },
                "required": [
                    "author",
                    "description",
                    "icon",
                    "name",
                    "plugin_id",
                    "status",
                    "tags",
                    "version"
                ]
            },
            "RequestPluginsSettingsSave": {
                "type": "object",
                "properties": {
                    "plugin_id": {
                        "type": "string",
                        "example": "encoder_video_hevc_vaapi"
                    },
                    "settings": {
                        "description": "The plugin settings",
                        "type": "array",
                        "items": {
                            "$ref": "#/components/schemas/PluginsConfigInputItem"
                        }
                    },
                    "library_id": {
                        "type": "integer",
                        "default": 0,
                        "example": 1
                    }
                },
                "required": [
                    "plugin_id",
                    "settings"
                ]
            },
            "RequestPluginsSettingsReset": {
                "type": "object",
                "properties": {
                    "plugin_id": {
                        "type": "string",
                        "example": "encoder_video_hevc_vaapi"
                    },
                    "library_id": {
                        "type": "integer",
                        "default": 0,
                        "example": 1
                    }
                },
                "required": [
                    "plugin_id"
                ]
            },
            "PluginsMetadataInstallableResults": {
                "type": "object",
                "properties": {
                    "plugin_id": {
                        "type": "string",
                        "description": "The plugin ID",
                        "example": "encoder_video_h264_nvenc"
                    },
                    "name": {
                        "type": "string",
                        "description": "The plugin name",
                        "example": "Video Encoder H264 - h264_nvenc"
                    },
                    "author": {
                        "type": "string",
                        "description": "The plugin author",
