{
  "openapi": "3.0.3",
  "info": {
    "title": "Device Groups API",
    "description": "This API allows to create and manage groups of IoT devices and other items.",
    "contact": {
      "name": "DISIT Lab",
      "url": "https://www.disit.org/",
      "email": "paolo.nesi@unifi.it"
    },
    "license": {
      "name": "GNU General Public License, version 2",
      "url": "https://www.gnu.org/licenses/old-licenses/gpl-2.0.html"
    },
    "version": "1.0.0"
  },
  "servers": [
    {
      "url": "https://www.disit.org/test/datamanager/api/v1",
      "description": "Test"
    },
    {
      "url": "https://www.snap4city.org/mypersonaldata/grp/api/v1",
      "description": "Production"
    }
  ],
  "paths": {
    "/devicegroup/": {
      "get": {
        "tags": [
          "List"
        ],
        "summary": "List your own groups",
        "parameters": [
          {
            "name": "sourceRequest",
            "in": "query",
            "description": "The category of the software module from where the request originates",
            "required": true,
            "schema": {
              "type": "string",
              "example": "Web"
            }
          },
          {
            "name": "sourceId",
            "in": "query",
            "description": "The unique identifier of software module from where the request originates",
            "required": false,
            "schema": {
              "type": "string",
              "example": "Swagger Try it out"
            }
          },
          {
            "name": "lang",
            "in": "query",
            "description": "The requester's locale",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "pageNumber",
            "in": "query",
            "description": "The zero-based page number, for paginated requests. Skip to get the full (unsorted) listing.",
            "required": false,
            "schema": {
              "type": "integer",
              "default": -1
            }
          },
          {
            "name": "pageSize",
            "in": "query",
            "description": "The number of results per page. Only used in paginated requests.",
            "required": false,
            "schema": {
              "type": "integer",
              "default": 10
            }
          },
          {
            "name": "sortBy",
            "in": "query",
            "description": "The property to be used for sorting results",
            "required": false,
            "schema": {
              "type": "string",
              "enum": [
                "id",
                "description",
                "updateTime",
                "highLevelType",
                "name",
                "ownership",
                "username"
              ],
              "default": "id"
            }
          },
          {
            "name": "sortDirection",
            "in": "query",
            "description": "The direction of sorting",
            "required": false,
            "schema": {
              "type": "string",
              "enum": [
                "asc",
                "desc"
              ],
              "default": "desc"
            }
          },
          {
            "name": "searchKey",
            "in": "query",
            "description": "For performing full-text searches.",
            "required": false,
            "schema": {
              "type": "string",
              "default": ""
            }
          },
          {
            "name": "highLevelType",
            "in": "query",
            "description": "For filtering results by type.",
            "required": false,
            "schema": {
              "type": "string",
              "default": ""
            }
          },
          {
            "name": "accessToken",
            "in": "query",
            "description": "An appropriate text through which the requester authenticates",
            "required": true,
            "schema": {
              "type": "string",
              "example": "eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJYQmNYSDJQbHdoeFhXeXlGdmRtdnhZeGVIOTZwQl80ZGZjU3VZQWFUQk9JIn0..MOu-XeCdt6M7ZJiYjQyo_hAtV3xEtmRaL6cxE-YdpsEoirm-QrQztPWFx0R6I7j6gkUuxum59zYSRS9ZPq9T207db7oKTqTEL_9WsrJXG0aCbkmpSL3En3q2zyiCKfbA4YrPYciHJkTF8LFhTgPWaXII7IwiyumDvcyl7wq_qDMJsihfM_Ewu86CrQzNs7UBirb40JiA6DeBipkI46T57E93FhA3lmRw_6IcDVVKxAiy5eQWpFwVJ7-g_jr7LW4AmhI_dSwwnfYI3JViX2V2Jw0AsveCb7sPAApAU3JdA3t2GpdGsjJDrNYaxrtq3yL7wzTQbYWvRzjp1309qK1YKw"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "The listing of groups is returned",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "type": "object",
                    "properties": {
                      "id": {
                        "type": "integer",
                        "example": 21
                      },
                      "description": {
                        "type": "string",
                        "example": "Devices deployed on that given place for that given project"
                      },
                      "insertTime": {
                        "type": "integer",
                        "example": 1590593353000
                      },
                      "updateTime": {
                        "type": "integer",
                        "example": 1590593372000
                      },
                      "highLevelType": {
                        "type": "string",
                        "example": "MyGroup"
                      },
                      "name": {
                        "type": "string",
                        "example": "Devices Project Alpha"
                      },
                      "organizations": {
                        "type": "string",
                        "example": "[ou=Antwerp,dc=foo,dc=example,dc=org]"
                      },
                      "ownership": {
                        "type": "string",
                        "enum": [
                          "private",
                          "public"
                        ],
                        "example": "private"
                      },
                      "username": {
                        "type": "string",
                        "example": "mirco"
                      },
                      "viewEnabled": {
                        "type": "boolean",
                        "example": true
                      },
                      "size": {
                        "type": "string",
                        "example": "2 items"
                      },
                      "enableEdit": {
                        "type": "boolean",
                        "example": false
                      }
                    }
                  }
                }
              }
            }
          },
          "204": {
            "description": "No groups found"
          },
          "400": {
            "description": "Malformed request"
          },
          "401": {
            "description": "Authentication failed"
          },
          "500": {
            "description": "Unexpected error"
          }
        }
      },
      "post": {
        "tags": [
          "Manage"
        ],
        "summary": "Create a group",
        "parameters": [
          {
            "name": "sourceRequest",
            "in": "query",
