              "default": "en"
            }
          },
          {
            "name": "accessToken",
            "in": "query",
            "description": "An appropriate text through which the requester authenticates",
            "required": true,
            "schema": {
              "type": "string",
              "example": "eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJYQmNYSDJQbHdoeFhXeXlGdmRtdnhZeGVIOTZwQl80ZGZjU3VZQWFUQk9JIn0..MOu-XeCdt6M7ZJiYjQyo_hAtV3xEtmRaL6cxE-YdpsEoirm-QrQztPWFx0R6I7j6gkUuxum59zYSRS9ZPq9T207db7oKTqTEL_9WsrJXG0aCbkmpSL3En3q2zyiCKfbA4YrPYciHJkTF8LFhTgPWaXII7IwiyumDvcyl7wq_qDMJsihfM_Ewu86CrQzNs7UBirb40JiA6DeBipkI46T57E93FhA3lmRw_6IcDVVKxAiy5eQWpFwVJ7-g_jr7LW4AmhI_dSwwnfYI3JViX2V2Jw0AsveCb7sPAApAU3JdA3t2GpdGsjJDrNYaxrtq3yL7wzTQbYWvRzjp1309qK1YKw"
            }
          }
        ],
        "requestBody": {
          "description": "Metadata of the new group",
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": [
                  "highLevelType",
                  "name",
                  "description"
                ],
                "properties": {
                  "highLevelType": {
                    "type": "string",
                    "enum": [
                      "MyGroup"
                    ],
                    "example": "MyGroup"
                  },
                  "name": {
                    "type": "string",
                    "example": "one"
                  },
                  "description": {
                    "type": "string",
                    "example": "Description of group one"
                  }
                }
              },
              "example": "{ \"highLevelType\": \"MyGroup\", \"name\": \"one\", \"description\": \"Description of group one\" }"
            }
          }
        },
        "responses": {
          "200": {
            "description": "Group created"
          },
          "400": {
            "description": "Malformed request"
          },
          "401": {
            "description": "Authentication failed"
          }
        }
      }
