    "/actions2": {
      "get": {
        "summary": "Friendly",
        "description": "Return a user list",
        "tags": [
          "Actions2 Group"
        ],
        "parameters": [
          {
            "in": "query",
            "name": "userId",
            "required": true,
            "schema": {
              "type": "integer",
              "format": "int64",
              "example": 123456,
              "readOnly": true
            },
            "description": "The ID of the user to look up"
          },
          {
            "in": "query",
            "name": "addExtra",
            "required": false,
            "schema": {
              "type": "boolean",
              "example": true
            },
            "description": "Should extra details be reported"
          }
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "oneOf": [
                    {
                      "type": "string",
                      "description": "A successful result"
                    },
                    {
                      "example": "Some dynamic message like this.",
                      "type": "string",
                      "description": "Alternate success"
                    },
                    {
                      "example": 93.45,
                      "type": "number",
                      "format": "double",
                      "description": "Alternate success (2)"
                    },
                    {
                      "example": {
                        "weatherPerson": "Joaquin",
                        "days": [
                          {
                            "tempCelsius": 21,
                            "windSpeedMph": 392
                          }
                        ]
                      },
                      "type": "object",
                      "properties": {
                        "weatherPerson": {
                          "example": "Joaquin",
                          "type": "string"
                        },
                        "days": {
                          "example": [
                            {
                              "tempCelsius": 21,
                              "windSpeedMph": 392
                            }
                          ],
                          "type": "array",
                          "items": {
                            "type": "object",
                            "properties": {
                              "tempCelsius": {
                                "example": 21,
                                "type": "integer",
                                "format": "int64"
                              },
                              "windSpeedMph": {
                                "example": 392,
                                "type": "integer",
                                "format": "int64"
                              }
                            }
                          }
                        }
                      },
                      "description": "Another success (partial content)"
                    }
                  ]
                }
              }
            }
          },
          "206": {
            "description": "Done/Success (partial)",
            "content": {
              "text/html": {
                "schema": {
                  "type": "string",
                  "description": "Human readable result"
                }
              },
              "application/json": {
                "schema": {
                  "type": "number",
                  "default": 123,
                  "description": "The **123** result"
                }
              }
            }
          },
          "404": {
            "description": "No user with the specified ID was found in the database"
          },
          "500": {
            "description": "An unexpected error occurred"
          }
        }
      }
    },
