paths:
  tmp0:
    post:
      description: Add Tag to system in McAfee ePO
      parameters:
      - description: TargetSystemName
        example: test-computer
        in: query
        name: TargetSystemName
        required: true
        schema: {type: string}
      - description: TagName
        example: quarantine
        in: query
        name: TagName
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: McAfee ePO AddTag
  tmp1:
    post:
      description: Clear Tag on system in ePO
      parameters:
      - description: TargetSystemName
        example: test-computer
        in: query
        name: TargetSystemName
        required: true
        schema: {type: string}
      - description: TagName
        example: quarantine
        in: query
        name: TagName
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: McAfee ePO ClearTag
  tmp2:
    post:
      description: Pull host information from McAfee ePO
      parameters:
      - description: TargetSystem to search. Search using name, IP address, MAC address,
          user name, agent GUID or tag
        example: 192.168.1.1
        in: query
        name: TargetSystem
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp2'}
          description: Successful request
      summary: McAfee Host Info
