                    "type": "string",
                    "description": "LDAP search base to use for group membership search (eg: ou=Groups,dc=example,dc=org)",
                    "x-vault-displayAttrs": {
                      "name": "Group DN"
                    }
                  },
                  "groupfilter": {
                    "type": "string",
