openapi: 3.0.2
paths:
  tmp0:
    post:
      description: SentinelOne Add IoC hash to blacklist.
      parameters:
      - description: Agent that Reported the IOC event
        example: '225494730938493804'
        in: query
        name: Agent ID
        required: true
        schema: {type: string}
      - description: List of hashes to add to black list
        example: ''
        in: query
        name: Hash
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: SentinelOne Blacklist Hash
  tmp1:
    post:
      description: Connect all agents matching the input filter to the network.
      parameters:
      - description: Migration status of agent (Failed, N/A, Pending, or Migrated)
        example: Failed
        in: query
        name: Migration Status
        required: false
        schema: {type: string}
      - description: Agent's universally unique identifier
        example: ff819e70af13be381993075eb0ce5f2f6de05be2
        in: query
        name: UUID
        required: false
        schema: {type: string}
      - description: Scan status of the agent (started, none, finished, or aborted)
        example: started
        in: query
        name: Scan Status
        required: false
        schema: {type: string}
      - description: Match computer name partially (substring)
        example: Lab1
        in: query
        name: Computer Name Like
        required: false
        schema: {type: string}
      - description: Included OS types
        example: windows
        in: query
        name: OS Types
        required: false
        schema: {type: string}
      - description: A list of agent IDs
        example: 225494730938493804,225494730938493915
        in: query
        name: IDs
        required: false
        schema: {type: string}
      - description: List of Group IDs to filter by
        example: 225494730938493804,225494730938493915
        in: query
        name: Group IDs
        required: false
        schema: {type: string}
      - description: Included network domains
        example: mybusiness.net,workgroup
        in: query
        name: Domains
        required: false
        schema: {type: string}
      - description: List of site IDs to filter by
        example: 225494730938493804,225494730938493915
        in: query
        name: Site IDs
        required: false
        schema: {type: string}
      - description: Included machine types
        example: laptop,desktop
        in: query
        name: Machine Types
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: SentinelOne Connect Agents
  tmp2:
    post:
      description: Disconnect agents from the network.
      parameters:
      - description: List of site IDs to filter by
        example: 225494730938493804,225494730938493915
        in: query
        name: Site IDs
        required: false
        schema: {type: string}
      - description: A list of agent IDs
        example: 225494730938493804,225494730938493915
        in: query
        name: IDs
        required: false
        schema: {type: string}
      - description: Only include uninstalled agents
        example: ''
        in: query
        name: Is Uninstalled
        required: false
        schema: {type: boolean}
      - description: Include active, decommissioned, or both
        example: true,false
        in: query
        name: Is Decommissioned
        required: false
        schema: {type: string}
      - description: A free-text search term, will match applicable attributes (substring)
        example: Linux
        in: query
        name: Query
        required: false
        schema: {type: string}
      - description: A list of network groups
        example: 225494730938493804,225494730938493915
        in: query
        name: Group IDs
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp2'}
          description: Successful request
      summary: SentinelOne Disconnect Agents
  tmp3:
    post:
      description: Retrieve running applications for a specific agent.
      parameters:
      - description: Agent ID list
        example: '225494730938493804'
        in: query
        name: Agent ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp3'}
          description: Successful request
      summary: SentinelOne Get Agent Applications
  tmp4:
    post:
      description: Retrieve information about agents.
      parameters:
      - description: Included network domains
        example: mybusiness.net,workgroup
        in: query
        name: Domains
        required: false
        schema: {type: string}
      - description: Free-test filter by visible IP (supports multiple values)
        example: 205,127.0
        in: query
        name: External IP Contains
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp4'}
          description: Successful request
      summary: SentinelOne Get Agents
  tmp5:
    post:
      description: Return the classification of a hash.
      parameters:
      - description: The hash to check the reputation of
        example: ddd5030a3d029f3845fc1052419829f08f312240
        in: query
        name: Hash
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp5'}
          description: Successful request
      summary: SentinelOne Get Hash
  tmp6:
    post:
      description: Get threats using a variety of filters.
      parameters:
      - description: Content hash of the threat to get
        example: ddd5030a3d029f3845fc1052419829f08f312240
        in: query
        name: Content Hash
        required: false
        schema: {type: string}
      - description: List of collection IDs to search
        example: 225494730938493804,225494730938493915
        in: query
        name: Collection IDs
        required: false
        schema: {type: string}
      - description: Status of the threat (suspicious, mitigated, suspicious_resolved,
          active, pending, or blocked)
        example: suspicious
        in: query
        name: Status
        required: false
        schema: {type: string}
      - description: List of groups to search for
        example: 0B0EFFE24B359FAF,2C9BAB7614F9B77F
        in: query
        name: Malicious Group IDs
        required: false
        schema: {type: string}
      - description: List of site IDs to filter by
        example: 225494730938493804,225494730938493915
        in: query
        name: Site IDs
        required: false
        schema: {type: string}
      - description: List of threat IDs
        example: 225494730938493804,225494730938493915
        in: query
        name: IDs
        required: false
        schema: {type: string}
      - description: ID of the agent associated with the threats
        example: '225494730938493804'
        in: query
        name: Agent ID
        required: false
        schema: {type: string}
      - description: Limit number of returned items (1-100)
        example: '10'
        in: body
        name: Limit
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp6'}
          description: Successful request
      summary: SentinelOne Get Threats
  tmp7:
    post:
      description: Mark hashes as benign and threat as resolved.
      parameters:
      - description: Scope to be used for exclusions (group, site, or tenant)
        example: site
        in: query
        name: Target Scope
        required: true
        schema: {type: string}
      - description: Selected whitening option (file_hash, path, file-type, certificate,
          or browser-type)
        example: file_hash
        in: query
        name: Whitening Option
        required: false
        schema: {type: string}
      - description: Vigilance annotation
        example: ''
        in: query
        name: Annotation
        required: false
        schema: {type: string}
      - description: Vigilance annotation URL
        example: ''
        in: query
        name: Annotation URL
        required: false
        schema: {type: string}
      - description: Threat status (suspicious, mitigated, suspicious_resolved, active,
          pending, or blocked)
        example: suspicious
        in: query
        name: Status
        required: false
        schema: {type: string}
      - description: List of site IDs to filter by
        example: 225494730938493804,225494730938493915
        in: query
        name: Site IDs
        required: false
        schema: {type: string}
      - description: List of sha1 hashes to search for
        example: ddd5030a3d029f3845fc1052419829f08f312240
        in: query
        name: Content Hashes
        required: false
        schema: {type: string}
      - description: ID of agent associated with threat
        example: '225494730938493804'
        in: query
        name: Agent ID
        required: false
        schema: {type: string}
      - description: Included OS types
        example: windows
        in: query
        name: OS Types
        required: false
        schema: {type: string}
      - description: List of groups to search for
        example: 0B0EFFE24B359FAF,2C9BAB7614F9B77F
        in: query
        name: Malicious Group IDs
        required: false
        schema: {type: string}
      - description: List of collection IDs to search for
        example: 225494730938493804,225494730938493915
        in: query
        name: Collection IDs
        required: false
        schema: {type: string}
      - description: List of threat classifications to search (must be capitalized)
        example: Virus,Trojan
        in: query
        name: Classifications
        required: false
        schema: {type: string}
      - description: List of threat IDs
        example: 225494730938493804,225494730938493915
        in: query
        name: IDs
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp7'}
          description: Successful request
      summary: SentinelOne Mark as Benign
  tmp8:
    post:
      description: Mark suspicious threats as threats.
      parameters:
      - description: Scope to be used for exclusions (group, site, or tenant)
        example: site
        in: query
        name: Target Scope
        required: true
        schema: {type: string}
      - description: Selected whitening option (file_hash, path, file-type, certificate,
          or browser-type)
        example: file_hash
        in: query
        name: Whitening Option
        required: false
        schema: {type: string}
      - description: Vigilance annotation
        example: ''
