        "name": "x-redlock-auth",
        "type": "apiKey"
      }
    },
    "schemas": {
      "AbsoluteTimeRangeConfigModel": {
        "allOf": [
          { "$ref": "#/components/schemas/TimeRangeConfigModel" },
          {
            "description": "Model for AbsoluteTimeRangeConfig",
            "properties": {
              "type": { "example": "absolute", "type": "string" },
              "value": {
                "allOf": [
                  { "$ref": "#/components/schemas/TimeModel" },
                  { "description": "Time range object" }
                ]
              }
            },
            "required": ["value"],
            "type": "object"
          }
        ]
      },
      "AlertIdAndSeverity": {
        "properties": {
          "id": { "type": "string" },
          "severity": {
            "enum": ["INFORMATIONAL", "LOW", "MEDIUM", "HIGH", "CRITICAL"],
            "type": "string"
          }
        },
        "type": "object"
      },
      "DecoratedResourceInfo": {
        "description": "Model for resource info and status list",
        "properties": {
          "accountId": { "description": "Account ID", "type": "string" },
          "accountName": { "description": "Account name", "type": "string" },
          "cloudType": {
            "description": "Cloud type",
            "enum": ["ALL", "AWS", "AZURE", "GCP", "ALIBABA_CLOUD", "OCI"],
            "type": "string"
          },
          "id": { "description": "Resource ID", "type": "string" },
          "name": { "description": "Resource name", "type": "string" },
          "overallPassed": {
            "description": "All policies passed",
            "type": "boolean"
          },
          "regionId": { "description": "Region ID", "type": "string" },
          "regionName": { "description": "Region Name", "type": "string" },
          "rrn": {
            "allOf": [
              { "$ref": "#/components/schemas/RRNModel" },
              { "description": "RRN" }
            ]
          },
          "scannedPolicies": {
            "description": "Scanned policies",
            "items": { "$ref": "#/components/schemas/ScannedPolicyInfo" },
            "type": "array"
          }
        },
        "type": "object"
      },
      "DecoratedResourceInfoResult": {
        "description": "Model for resource info and status list",
        "properties": {
          "nextPageToken": {
            "allOf": [
              { "$ref": "#/components/schemas/ResourceInfoPageOption" },
              { "description": "Use as page token to return next page of data" }
            ]
          },
          "pageSize": {
            "description": "Page size",
            "format": "int64",
            "type": "integer"
          },
          "resources": {
            "description": "List of metadata for resources that match filter",
            "items": { "$ref": "#/components/schemas/DecoratedResourceInfo" },
            "type": "array"
          },
          "timestamp": {
            "description": "Epoch timestamp",
            "format": "int64",
            "type": "integer"
          },
          "totalMatchedCount": {
            "description": "Total number of filtered resources, both scanned and unscanned",
            "format": "int64",
            "type": "integer"
          }
        },
        "type": "object"
      },
      "ExternalFindingView": {
        "properties": {
          "accountId": { "type": "string" },
          "count": { "type": "string" },
          "createdOn": { "format": "int64", "type": "integer" },
          "customerId": { "format": "int32", "type": "integer" },
          "cveId": { "type": "string" },
          "description": { "type": "string" },
          "externalFindingId": { "format": "int64", "type": "integer" },
          "findingId": { "type": "string" },
          "normalizedName": { "type": "string" },
          "normalizedNames": { "items": { "type": "string" }, "type": "array" },
          "nvdUrl": { "type": "string" },
          "rawData": { "type": "string" },
          "regionId": { "type": "string" },
          "resourceCloudId": { "type": "string" },
          "resourceId": { "format": "int64", "type": "integer" },
          "resourceUrl": { "type": "string" },
          "riskFactors": {
            "items": {
              "enum": [
                "CRITICAL_SEVERITY",
                "HIGH_SEVERITY",
                "MEDIUM_SEVERITY",
                "HAS_FIX",
                "REMOTE_EXECUTION",
                "DOS",
                "RECENT_VULNERABILITY",
                "EXPLOIT_EXISTS",
                "ATTACK_COMPLEXITY_LOW",
                "ATTACK_VECTOR_NETWORK",
                "REACHABLE_FROM_THE_INTERNET",
                "LISTENING_PORTS",
                "CONTAINER_IS_RUNNING_AS_ROOT",
                "NO_MANDATORY_SECURITY_PROFILE_APPLIED",
                "RUNNING_AS_PRIVILEGED_CONTAINER",
                "PACKAGE_IN_USE"
              ],
              "type": "string"
            },
            "type": "array",
            "uniqueItems": true
          },
          "rlUpdatedOn": { "format": "int64", "type": "integer" },
          "scanId": { "type": "string" },
          "score": { "type": "object" },
          "severity": {
            "enum": ["INFORMATIONAL", "LOW", "MEDIUM", "HIGH", "CRITICAL"],
            "type": "string"
          },
          "source": {
            "enum": [
              "AWS_INSPECTOR",
              "AWS_GUARD_DUTY",
              "TENABLE",
              "QUALYS",
              "PRISMA_CLOUD",
              "AZURE_SECURITY_CENTER"
            ],
            "type": "string"
          },
          "sourceData": {
            "additionalProperties": { "type": "object" },
            "type": "object"
          },
          "status": {
            "enum": [
              "PENDING",
              "NO_ERROR",
              "ERROR",
              "ENABLED",
              "DISABLED",
              "OPEN",
              "DISMISSED",
              "RESOLVED",
              "DESCOPED",
              "RISK_SCORING_ERROR"
            ],
            "type": "string"
          },
          "title": { "type": "string" },
          "type": {
            "enum": [
