          "cloudType": {
            "description": "Cloud Type",
            "enum": ["ALL", "AWS", "AZURE", "GCP", "ALIBABA_CLOUD", "OCI"],
            "type": "string"
          },
          "createdTs": {
            "description": "Creation timestamp",
            "format": "int64",
            "type": "integer"
          },
          "data": {
