          },
          "hasNetwork": { "description": "Has network", "type": "boolean" },
          "id": { "description": "ID", "type": "string" },
          "insertTs": {
            "description": "Insertion timestamp",
            "format": "int64",
            "type": "integer"
          },
          "name": { "description": "Name", "type": "string" },
          "regionId": { "description": "Region ID", "type": "string" },
          "regionName": { "description": "Region name", "type": "string" },
          "resourceType": { "description": "Resource type", "type": "string" },
          "riskGrade": { "description": "Risk grade", "type": "string" },
          "rrn": {
