          },
          "service": { "description": "Service", "type": "string" },
          "stateId": { "description": "State ID", "type": "string" },
          "tags": {
            "additionalProperties": { "type": "string" },
            "description": "Tags",
            "type": "object"
          },
          "url": { "description": "URL", "type": "string" },
          "vpcId": {
