          },
          "confirmation": {
            "$ref": "#/components/schemas/hrefType"
          },
          "startAuthorisation": {
            "$ref": "#/components/schemas/hrefType"
          },
          "startAuthorisationWithPsuIdentification": {
            "$ref": "#/components/schemas/hrefType"
