          "startAuthorisationWithAuthenticationMethodSelection": {
            "$ref": "#/components/schemas/hrefType"
          },
          "startAuthorisationWithTransactionAuthorisation": {
            "$ref": "#/components/schemas/hrefType"
          },
          "self": {
            "$ref": "#/components/schemas/hrefType"
          },
          "status": {
            "$ref": "#/components/schemas/hrefType"
          },
          "scaStatus": {
            "$ref": "#/components/schemas/hrefType"
