                "tags": [
                    "Manifests"
                ],
                "summary": "Request a download link to a manifest file and redirect",
                "description": "\nInitiate and check status of a manifest generation job, returning\neither a 301 response redirecting to a URL to re-check the status of\nthe manifest generation or a 302 response redirecting to the location\nof the completed manifest.\n\nThis endpoint is not suitable for interactive use via the Swagger UI.\nPlease use the [/fetch endpoint](#operations-Manifests-get_fetch_manifest_files)\ninstead.\n",
                "responses": {
                    "301": {
                        "description": "\nThe manifest generation has been started or is ongoing.\nThe response is a redirect back to this endpoint, so the client\nshould expect a subsequent response of the same kind.\n",
                        "headers": {
                            "Location": {
                                "description": "URL to recheck the status of the manifest generation.",
                                "schema": {
                                    "type": "string",
                                    "format": "url"
                                }
                            },
                            "Retry-After": {
                                "description": "Recommended number of seconds to wait before requesting the URL specified in the Location header.",
                                "schema": {
                                    "type": "string"
                                }
                            }
                        }
                    },
                    "302": {
                        "description": "\nThe manifest generation is complete and ready for download.\n",
                        "headers": {
                            "Location": {
                                "description": "URL that will yield the actual manifest file.",
                                "schema": {
                                    "type": "string",
                                    "format": "url"
                                }
                            },
                            "Retry-After": {
                                "description": "Recommended number of seconds to wait before requesting the URL specified in the Location header.",
                                "schema": {
                                    "type": "string"
                                }
                            }
                        }
                    },
                    "410": {
                        "description": "\nThe manifest associated with the `objectKey` in this request has\nexpired. Request a new manifest.\n"
                    }
                }
            }
