{
    "openapi": "3.0.0",
    "info": {
        "title": "Marketplace API",
        "description": "The Marketplace APIs are a set of API requests used by marketplaces and sellers in their operations.",
        "contact": {},
        "version": "1.0"
    },
    "servers": [
        {
            "url": "/",
            "description": ""
        }
    ],
    "paths": {
        "/{accountName}.{environment}.com.br/api/notificator/{sellerId}/changenotification/{skuId}/price": {
            "post": {
                "tags": [
                    "Notification"
                ],
                "summary": "Notify marketplace of price update",
                "description": "This endpoint is used by *sellers* to notify marketplaces that the price has changed for one of their SKUs. \n\nThere is no request body in this call, indicating the new price value, for instance. It only notifies a specific marketplace (`accountName`) that a seller (`sellerId`) has changed the price of an SKU (`skuId`). \n\n*Marketplaces* will then call the [fulfillment endpoint](https://developers.vtex.com/vtex-rest-api/reference/fulfillment-simulation) provided in the seller registration form to get the updated price information.",
                "operationId": "PriceNotification",
                "parameters": [
                    {
                        "name": "accountName",
                        "in": "path",
                        "required": true,
                        "description": "Name of the VTEX account that belongs to the marketplace. The notification will be posted into this account.",
                        "schema": {
                            "type": "string",
                            "default": "apiexamples"
                        }
                    },
                    {
                        "name": "Content-Type",
                        "in": "header",
                        "description": "Describes the type of the content being sent.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    },
                    {
                        "name": "environment",
                        "in": "path",
                        "required": true,
                        "description": "Environment to use. Used as part of the URL.",
                        "schema": {
                            "type": "string",
                            "default": "vtexcommercestable"
                        }
                    },
                    {
                        "name": "Accept",
                        "in": "header",
                        "description": "HTTP Client Negotiation Accept Header. Indicates the types of responses the client can understand.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    },
                    {
                        "name": "sellerId",
                        "in": "path",
                        "description": "A string that identifies the seller in the marketplace. This ID must be created by the marketplace and informed to the seller before the integration is built.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "seller123"
                        }
                    },
                    {
                        "name": "skuId",
                        "in": "path",
                        "description": "A string that identifies the seller's SKU that suffered the change. This is the ID that the marketplace will use for all  references to this SKU, such as price and inventory notifications.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "1234"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "OK",
                        "headers": {}
                    }
                },
                "deprecated": false
            }
        },
        "/{accountName}.{environment}.com.br/api/notificator/{sellerId}/changenotification/{skuId}/inventory": {
            "post": {
                "tags": [
                    "Notification"
                ],
                "summary": "Notify marketplace of inventory update",
                "description": "This endpoint is used by *sellers* to notify marketplaces that the inventory level has changed for one of their SKUs. \n\nThere is no request body in this call, indicating the new inventory level, for instance. It only notifies a specific marketplace (`accountName`) that a seller (`sellerId`) has changed the inventory level of an SKU (`skuId`). \n\n*Marketplaces* will then call the [fulfillment endpoint](https://developers.vtex.com/vtex-rest-api/reference/fulfillment-simulation) provided in the seller registration form to get the updated inventory  information.",
                "operationId": "InventoryNotification",
                "parameters": [
                    {
                        "name": "accountName",
                        "in": "path",
                        "required": true,
                        "description": "Name of the VTEX account that belongs to the marketplace. The notification will be posted into this account.",
                        "schema": {
                            "type": "string",
                            "default": "apiexamples"
                        }
                    },
                    {
                        "name": "environment",
                        "in": "path",
                        "required": true,
                        "description": "Environment to use. Used as part of the URL.",
                        "schema": {
                            "type": "string",
                            "default": "vtexcommercestable"
                        }
                    },
                    {
                        "name": "Accept",
                        "in": "header",
                        "description": "HTTP Client Negotiation Accept Header. Indicates the types of responses the client can understand.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    },
                    {
                        "name": "Content-Type",
                        "in": "header",
                        "description": "Describes the type of the content being sent.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    },
                    {
                        "name": "sellerId",
                        "in": "path",
                        "description": "A string that identifies the seller in the marketplace. This ID must be created by the marketplace and informed to the seller before the integration is built.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "seller123"
                        }
                    },
                    {
                        "name": "skuId",
                        "in": "path",
                        "description": "A string that identifies the SKU in the seller, that suffered the change. This is the ID that the marketplace will use for all  references to this SKU, such as price and inventory notifications.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "1234"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "",
                        "headers": {}
                    }
                },
                "deprecated": false
            }
        },
        "/api.vtex.com/{accountName}/suggestions/configuration/autoapproval/toggle": {
            "get": {
                "tags": [
                    "SKU Approval Settings"
                ],
                "summary": "Get autoApprove Status in Account Settings",
                "description": "This endpoint can be used to check whether the autoapprove setting is active or not, for a specific seller. If the response is `true`, the autoapprove setting is active. If the response is `false`, it is inactive.",
                "operationId": "GetautoApprovevaluefromconfig",
                "parameters": [
                    {
                        "name": "sellerId",
                        "in": "query",
                        "description": "A string that identifies the seller in the marketplace. This ID must be created by the marketplace.",
                        "required": true,
                        "style": "form",
                        "schema": {
                            "type": "string",
                            "default": "seller123"
                        }
                    },
                    {
                        "name": "accountName",
                        "in": "path",
                        "required": true,
                        "description": "Name of the VTEX account that belongs to the marketplace. All data extracted, and changes added will be posted into this account.",
                        "schema": {
                            "type": "string",
                            "default": "apiexamples"
                        }
                    },
                    {
                        "name": "Accept",
                        "in": "header",
                        "description": "HTTP Client Negotiation Accept Header. Indicates the types of responses the client can understand.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    },
                    {
                        "name": "Content-Type",
                        "in": "header",
                        "description": "Describes the type of the content being sent.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "OK",
                        "content": {
                            "application/json": {
                                "schema": {
                                            "example": {
                                                        "Enabled": false
                                                },
                                            "type": "object",
                                            "properties": {
                                                    "Enabled": {
                                                        "type": "boolean",
                                                        "description": "If the autoapprove setting is active or not."}
                                                    }

                                        }
                                }
                            }
                    }
                },
                "deprecated": false
            },
            "put": {
                "tags": [
                    "SKU Approval Settings"
                ],
                "summary": "Activate autoApprove in Marketplace's Account",
                "description": "This endpoint enables the autoapprove rule to a marketplace's whole Received SKUs module. Once enabling the rule, received SKUs will be automatically approved on your store, regardless of the seller. For the autoapprove rule to work as expected, the approval [Matcher score](https://help.vtex.com/en/tutorial/entendendo-a-pontuacao-do-vtex-matcher--tutorials_424) should be set up as 80 (default value), but you can configure a different number through the field `Score` in [Save Account's Approval Settings](https://developers.vtex.com/vtex-rest-api/reference/saveaccountconfig).",
                "operationId": "Saveautoapproveforaccount",
                "parameters": [
                    {
                        "name": "accountName",
                        "in": "path",
                        "required": true,
                        "description": "Name of the VTEX account that belongs to the marketplace. All data extracted, and changes added will be posted into this account.",
                        "schema": {
                            "type": "string",
                            "default": "apiexamples"
                        }
                    },
                    {
                        "name": "Accept",
                        "in": "header",
                        "description": "HTTP Client Negotiation Accept Header. Indicates the types of responses the client can understand.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    },
                    {
                        "name": "Content-Type",
                        "in": "header",
                        "description": "Describes the type of the content being sent.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    }
                ],
                "requestBody": {
                    "description": "",
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/SaveautoapproveforaccountRequest"
                            },
                            "example": {
                                "Enabled": true
                            }
                        }
                    },
                    "required": true
                },
                "responses": {
                    "200": {
                        "description": "OK",
                        "headers": {},
                        "content": {
                            "application/json": {
                                "schema": {
                                            "example": {
                                                        "Enabled": false
                                                },
                                            "type": "object",
                                            "properties": {
                                                    "Enabled": {
                                                        "type": "boolean",
                                                        "description": "If the autoapprove setting is active or not."}
                                                    }

                                        }
                                }
                            }

                    }
                },
                "deprecated": false
            }
        },
        "/api.vtex.com/{accountName}/suggestions/configuration": {
            "get": {
                "tags": [
                    "SKU Approval Settings"
                ],
                "summary": "Get Account's Approval Settings",
                "description": "This endpoint retrieves the current approval settings of a marketplace’s Received SKUs module.  Its response includes: \n\n- `Score`: Matcher scores for approving and rejecting SKUs received from sellers. \n\n- `Matchers`: All Matchers configured on the marketplace, and their respective details. \n\n- `SpecificationsMapping`: Mapping of product and SKU specifications, per seller. \n\n- `MatchFlux`: This field determines the type of approval configuration applied to SKUs received  from a seller. The possible values include:  \n\n`default`, where the Matcher reviews the SKU, and approves it based on its score. \n\n`manual`, for manual approvals through the Received SKU UI, or Match API. \n\n`autoApprove`, for every SKU received from a given seller to be approved automatically, regardless of their Matcher Score.",
                "operationId": "Getaccountconfig",
                "parameters": [
                    {
                        "name": "accountName",
                        "in": "path",
                        "required": true,
                        "description": "Name of the VTEX account that belongs to the marketplace. All data extracted, and changes added will be posted into this account.",
                        "schema": {
                            "type": "string",
                            "default": "apiexamples"
                        }
                    },
                    {
                        "name": "Accept",
                        "in": "header",
                        "description": "HTTP Client Negotiation Accept Header. Indicates the types of responses the client can understand.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    },
                    {
                        "name": "Content-Type",
                        "in": "header",
                        "description": "Describes the type of the content being sent.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "OK",
                        "headers": {},
                        "content": {
                            "application/json": {
                                "schema": {
                                            "example": {
                                                "Score": {
                                                    "Approve": 80,
                                                    "Reject": 30
                                                },
                                                "Matchers": [
                                                    {
                                                        "MatcherId": "vtex-matcher",
                                                        "hook-base-address": "http://portal.vtexinternal.com/api/ssm/hooks",
                                                        "IsActive": true,
                                                        "UpdatesNotificationEndpoint": null,
                                                        "Description": null
                                                    }
                                                ],
                                                "Rules": {
                                                    "Item": [],
                                                    "Product": []
                                                },
                                                "SpecificationsMapping": [],
                                                "MatchFlux": "Default"
                                            },
                                            "type": "object",
                                            "properties": {
                                                    "Score": {
                                                        "type": "object",
                                                        "description": "Matcher scores for approving and rejecting SKUs received from sellers."
                                                    },
                                                    "Matchers": {
                                                        "type":"array",
                                                        "description": "All Matchers configured on the marketplace, and their respective details."
                                                    },
                                                    "Rules": {
                                                        "type": "object",
                                                        "description": "Items and products."
                                                    },
                                                    "SpecificationsMapping": {
                                                        "type": "array",
                                                        "description": "Mapping of product and SKU specifications, per seller."
                                                    },
                                                    "MatchFlux": {
                                                        "type": "string",
                                                        "description": "This field determines the type of approval configuration applied to SKUs received from a seller."
                                                    }
                                            }

                                        }
                                }
                        }
                    }
                },
                "deprecated": false
            },
            "put": {
                "tags": [
                    "SKU Approval Settings"
                ],
                "summary": "Save Account's Approval Settings",
                "description": "Marketplaces use this endpoint to create or update approval settings on their Received SKUs module. The request includes all the details necessary to implement the chosen approval settings.",
                "operationId": "Saveaccountconfig",
                "parameters": [
                    {
                        "name": "accountName",
                        "in": "path",
                        "required": true,
                        "description": "Name of the VTEX account that belongs to the marketplace. All data extracted, and changes added will be posted into this account.",
                        "schema": {
                            "type": "string",
                            "default": "apiexamples"
                        }
                    },
                    {
                        "name": "Accept",
                        "in": "header",
                        "description": "HTTP Client Negotiation Accept Header. Indicates the types of responses the client can understand.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    },
                    {
                        "name": "Content-Type",
                        "in": "header",
                        "description": "Describes the type of the content being sent.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    }
                ],
                "requestBody": {
                    "description": "",
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/SaveaccountconfigRequest"
                            }
                        }
                    },
                    "required": true
                },
                "responses": {
                    "200": {
                        "description": "OK",
                        "headers": {}
                    }
                },
                "deprecated": false
            }
        },
        "/api.vtex.com/{accountName}/suggestions/configuration/seller/{sellerId}": {
            "get": {
                "tags": [
                    "SKU Approval Settings"
                ],
                "summary": "Get Seller's Approval Settings",
                "description": "This endpoint retrieves the current Received SKUs approval settings applied to a specific seller. Its response includes: \n\n- `sellerId`: A string that identifies the seller in the marketplace. \n\n- `accountId`: Marketplace’s account ID. \n\n- `accountName`: Marketplace’s account name. \n\n- `mapping`: Mapping of SKU and product Specifications. \n\n- `matchFlux`: This field determines the type of approval configuration applied to SKUs received  from a seller. The possible values include:  \n\n`default`, where the Matcher reviews the SKU, and approves it based on its score. \n\n`manual`, for manual approvals through the Received SKU UI and Match API. \n\n`autoApprove`, for every SKU received from a given seller to be approved automatically, regardless of the Matcher Score.",
                "operationId": "Getselleraccountconfig",
                "parameters": [
                    {
                        "name": "accountName",
                        "in": "path",
                        "required": true,
                        "description": "Name of the VTEX account that belongs to the marketplace. All data extracted, and changes added will be posted into this account.",
                        "schema": {
                            "type": "string",
                            "default": "apiexamples"
                        }
                    },
                    {
                        "name": "sellerId",
                        "in": "path",
                        "description": "A string that identifies the seller in the marketplace. This ID must be created by the marketplace.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "seller123"
                        }
                    },
                    {
                        "name": "Accept",
                        "in": "header",
                        "description": "HTTP Client Negotiation Accept Header. Indicates the types of responses the client can understand.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    },
                    {
                        "name": "Content-Type",
                        "in": "header",
                        "description": "Describes the type of the content being sent.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "OK",
                        "headers": {}
                    }
                },
                "deprecated": false
            },
            "put": {
                "tags": [
                    "SKU Approval Settings"
                ],
                "summary": "Save Seller's Approval Settings",
                "description": "Marketplaces use this endpoint to create or update approval settings to a specific seller, on the Received SKUs module. The request includes all the details necessary to implement the chosen approval settings.",
                "operationId": "Putselleraccountconfig",
                "parameters": [
                    {
                        "name": "accountName",
                        "in": "path",
                        "required": true,
                        "description": "Name of the VTEX account that belongs to the marketplace. All data extracted, and changes added will be posted into this account.",
                        "schema": {
                            "type": "string",
                            "default": "apiexamples"
                        }
                    },
                    {
                        "name": "sellerId",
                        "in": "path",
                        "description": "A string that identifies the seller in the marketplace. This ID must be created by the marketplace.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "seller123"
                        }
                    },
                    {
                        "name": "Accept",
                        "in": "header",
                        "description": "HTTP Client Negotiation Accept Header. Indicates the types of responses the client can understand.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    },
                    {
                        "name": "Content-Type",
                        "in": "header",
                        "description": "Describes the type of the content being sent.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    }
                ],
                "requestBody": {
                    "description": "",
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/PutselleraccountconfigRequest"
                            },
                            "example": {
                                "sellerId": "1a",
                                "mapping": null,
                                "matchFlux": "Default"
                            }
                        }
                    },
                    "required": true
                },
                "responses": {
                    "200": {
                        "description": "OK",
                        "headers": {}
                    }
                },
                "deprecated": false
            }
        },
        "/api.vtex.com/{accountName}/suggestions/configuration/autoapproval/toggle/seller/{sellerId}": {
            "put": {
                "tags": [
                    "SKU Approval Settings"
                ],
                "summary": "Activate autoApprove Setting for a Seller",
                "description": "This endpoint enables the auto approve setting to received SKUs from a specific seller. Be aware that once enabling the rule through this request, all received SKUs from that seller will be automatically approved on your store, regardless of the Matcher Score.",
                "operationId": "Saveautoapproveforaccountseller",
                "parameters": [
                    {
                        "name": "accountName",
                        "in": "path",
                        "required": true,
                        "description": "Name of the VTEX account that belongs to the marketplace. All data extracted, and changes added will be posted into this account.",
                        "schema": {
                            "type": "string",
                            "default": "apiexamples"
                        }
                    },
                    {
                        "name": "sellerId",
                        "in": "path",
                        "description": "A string that identifies the seller in the marketplace. This ID must be created by the marketplace.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "seller123"
                        }
                    },
                    {
                        "name": "Accept",
                        "in": "header",
                        "description": "HTTP Client Negotiation Accept Header. Indicates the types of responses the client can understand.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    },
                    {
                        "name": "Content-Type",
                        "in": "header",
                        "description": "Describes the type of the content being sent.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    }
                ],
                "requestBody": {
                    "description": "",
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/SaveautoapproveforaccountsellerRequest"
                            },
                            "example": {
                                "Enabled": true
                            }
                        }
                    },
                    "required": true
                },
                "responses": {
                    "200": {
                        "description": "OK",
                        "headers": {}
                    }
                },
                "deprecated": false
            }
        },
        "/api.vtex.com/{accountName}/suggestions": {
            "get": {
                "tags": [
                    "Get Suggestions"
                ],
                "summary": "Get all SKU suggestions",
                "description": "This endpoint retrieves a list of all SKUs sent by the seller for the Marketplace's approval. Marketplace operators should use this endpoint whenever they want to check the full list of received SKUs and their  information. \n\nNote that all the information sent by the seller will be in the [content] object. All remaining information in this endpoint's response is given by the Matcher. \n\nMatcher rates received SKUs by correlating the data sent by sellers, to existing fields in the marketplace. The calculation of these scores determines whether the product has been: \n\n`Approved`: score equal to or greater than 80 points. \n\n`Pending`: from 31 to 79 points.\n\n`Denied`: from 0 to 30 points. \n\nNote that  if the autoApprove setting is enabled, the SKUs will be approved, regardless of the Score.",
                "operationId": "Getsuggestions",
                "parameters": [
                    {
                        "name": "accountName",
                        "in": "path",
                        "required": true,
                        "description": "Name of the VTEX account. Used as part of the URL",
                        "schema": {
                            "type": "string",
                            "default": "apiexamples"
                        }
                    },
                    {
                        "name": "q",
                        "in": "query",
                        "description": "This field allows you to customize your search. You can fill in this query param if you want to narrow down your search using the available filters on Received SKU modules.",
                        "required": false,
                        "style": "form",
                        "explode": true,
                        "schema": {
                            "type": "string",
                            "example": ""
                        }
                    },
                    {
                        "name": "type",
                        "in": "query",
                        "description": "This field allows users to filter SKU suggestions, by searching only the new suggestions that were just sent, and suggestions that have already been sent, but were updated. Possible values for this field include `new` and `update`.",
                        "required": false,
                        "style": "form",
                        "explode": true,
                        "schema": {
                            "type": "string",
                            "example": "new"
                        }
                    },
                    {
                        "name": "seller",
                        "in": "query",
                        "description": "A string that identifies the seller in the marketplace. This ID must be created by the marketplace and informed to the seller so it can call this endpoint.",
                        "required": false,
                        "style": "form",
                        "explode": true,
                        "schema": {
                            "type": "string",
                            "example": ""
                        }
                    },
                    {
