{
  "openapi": "3.0.3",
  "info": {
    "description": "IdentityIQ REST Endpoint Interface Documentation for SCIM\n\n# Authentication\n\n<!-- ReDoc-Inject: <security-definitions> -->",
    "version": "8.3",
    "title": "IdentityIQ SCIM REST API"
  },
  "servers": [
    {
      "url": "http://localhost:8080/identityiq/scim/v2",
      "description": "IdentityIQ SCIM server basepath and path to API."
    }
  ],
  "tags": [
    {
      "name": "Accounts"
    },
    {
      "name": "Alerts"
    },
    {
      "name": "Applications",
      "description": "Alert object, a generic real time event processing object."
    },
    {
      "name": "Checked Policy Violations"
    },
    {
      "name": "Entitlements"
    },
    {
      "name": "LaunchedWorkflows"
    },
    {
      "name": "ObjectConfig"
    },
    {
      "name": "PolicyViolations"
    },
    {
      "name": "ResourceTypes"
    },
    {
      "name": "Roles"
    },
    {
      "name": "Schemas"
    },
    {
      "name": "ServiceProviderConfig"
    },
    {
      "name": "TaskResults"
    },
    {
      "name": "Users"
    },
    {
      "name": "Workflows"
    }
  ],
  "security": [
    {
      "basicAuth": []
    }
  ],
  "paths": {
    "/Accounts": {
      "get": {
        "operationId": "getAccounts",
        "tags": [
          "Accounts"
        ],
        "summary": "Returns all Account resources.",
        "description": "This endpoint returns all Account resources.",
        "parameters": [
          {
            "in": "query",
            "name": "filter",
            "schema": {
              "type": "string",
              "example": "application.value eq \"7f000001804444fe818051b44414444c\" and displayName sw \"G\""
            },
            "description": "Allows for query filters according to RFC-7644, Section 3.4.2.2 - not all operations are supported."
          },
          {
            "in": "query",
            "name": "sortBy",
            "schema": {
              "type": "string",
              "example": "displayName"
            },
            "description": "Allows sorting the results by a resource's attributes."
          },
          {
            "in": "query",
            "name": "sortOrder",
            "schema": {
              "type": "string",
              "example": "descending",
              "default": "ascending"
            },
            "description": "Determines what order to sort results in."
          },
          {
            "in": "query",
            "name": "startIndex",
            "schema": {
              "type": "integer",
              "example": 2313,
              "default": 1
            },
            "description": "Determines the starting index of the result set."
          },
          {
            "in": "query",
            "name": "count",
            "schema": {
              "type": "integer",
              "example": 10,
              "default": 1000
            },
            "description": "Specifies the number of results per page."
          },
          {
            "in": "query",
            "name": "attributes",
            "schema": {
              "type": "string",
              "example": "displayName,active"
            },
            "description": "A comma-separated list of attributes to return in the response. This query parameter supersedes excludedAttributes, so providing the same attribute(s) to both will result in the attribute(s) being returned."
          },
          {
            "in": "query",
            "name": "excludedAttributes",
            "schema": {
              "type": "string",
              "example": "displayName,active"
            },
            "description": "A comma-separated list of attributes to exclude from the response. *Some attributes cannot be excluded.*"
          }
        ],
        "responses": {
          "200": {
            "description": "Returns all Account resources.",
            "content": {
              "application/json": {
                "schema": {
                  "properties": {
                    "totalResults": {
                      "description": "Number of Account resources returned",
                      "type": "integer",
                      "example": "18,"
                    },
                    "startIndex": {
                      "description": "The starting index of the resource set list. Can be specified with startIndex query parameter.",
                      "type": "integer",
                      "example": 1
                    },
                    "schemas": {
                      "description": "SCIM Schema used for response.",
                      "type": "array",
                      "items": {
                        "type": "string"
                      },
                      "example": [
                        "urn:ietf:params:scim:api:messages:2.0:ListResponse"
                      ]
                    },
                    "Resources": {
                      "type": "array",
                      "items": {
                        "$ref": "#/components/schemas/accountResponse"
                      }
                    }
                  }
                }
              },
              "application/scim+json": {
                "schema": {
                  "properties": {
                    "totalResults": {
                      "description": "Number of Account resources returned.",
                      "type": "integer",
                      "example": "1500,"
                    },
                    "startIndex": {
                      "description": "The starting index of the resource set list. Can be specified with startIndex query parameter.",
                      "type": "integer",
                      "example": 1
                    },
                    "schemas": {
                      "description": "SCIM Schema used for response.",
                      "type": "array",
                      "items": {
                        "type": "string"
                      },
                      "example": [
                        "urn:ietf:params:scim:api:messages:2.0:ListResponse"
                      ]
                    },
                    "Resources": {
                      "description": "The SCIM resources returned for this endpoint.",
                      "type": "array",
                      "items": {
                        "$ref": "#/components/schemas/accountResponse"
                      }
                    }
                  }
                }
              }
            }
          }
        }
      },
      "post": {
        "operationId": "createAccount",
        "tags": [
          "Accounts"
        ],
        "summary": "Creates an Account.",
        "description": "The endpoint used to create an Account resource. The required payload fields can differ drastically depending on the Application.",
        "parameters": [
          {
            "in": "query",
            "name": "attributes",
            "schema": {
              "type": "string",
              "example": "displayName,active"
            },
            "description": "A comma-separated list of attributes to return in the response. This query parameter supersedes excludedAttributes, so providing the same attribute(s) to both will result in the attribute(s) being returned."
          },
          {
            "in": "query",
            "name": "excludedAttributes",
            "schema": {
              "type": "string",
              "example": "displayName,active"
            },
            "description": "A comma-separated list of attributes to exclude from the response. *Some attributes cannot be excluded.*"
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "application/scim+json": {
              "schema": {
                "$ref": "#/components/schemas/accountPostRequest"
              }
            },
            "*/*": {
              "schema": {
                "$ref": "#/components/schemas/accountPostRequest"
              }
            }
          }
        },
        "responses": {
          "201": {
            "description": "Creates an Account and returns the resultant Account.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/accountResponse"
                }
              }
            }
          }
        }
      }
    },
    "/Accounts/{accountId}": {
      "get": {
        "operationId": "getAccountById",
        "tags": [
          "Accounts"
        ],
        "summary": "Returns an Account resource based on id.",
        "description": "The Account resource with matching id is returned.",
        "parameters": [
          {
            "name": "accountId",
            "in": "path",
            "schema": {
              "type": "string",
              "example": "c7c7777c7ef77e77777ee77e7a1f0444"
            },
            "description": "The id of the Account.",
            "required": true
          },
          {
            "in": "query",
            "name": "attributes",
            "schema": {
              "type": "string",
              "example": "displayName,active"
            },
            "description": "A comma-separated list of attributes to return in the response. This query parameter supersedes excludedAttributes, so providing the same attribute(s) to both will result in the attribute(s) being returned."
          },
          {
            "in": "query",
            "name": "excludedAttributes",
            "schema": {
              "type": "string",
              "example": "displayName,active"
            },
            "description": "A comma-separated list of attributes to exclude from the response. *Some attributes cannot be excluded.*"
          }
        ],
        "responses": {
          "200": {
            "description": "Returns a single Account resource based on the id.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/accountResponse"
                }
              },
              "application/scim+json": {
                "schema": {
                  "$ref": "#/components/schemas/accountResponse"
                }
              }
            }
          }
        }
      },
      "put": {
        "operationId": "updateAccount",
        "tags": [
          "Accounts"
        ],
        "summary": "Updates an existing Account.",
        "description": "The endpoint used to update an Account resource. The required payload fields depend on the corresponding Application.",
        "parameters": [
          {
            "name": "accountId",
            "in": "path",
            "schema": {
              "type": "string",
              "example": "c7c7777c7ef77e77777ee77e7a1f0444"
            },
            "description": "The id of the Account.",
            "required": true
          },
          {
            "in": "query",
            "name": "attributes",
            "schema": {
              "type": "string",
              "example": "displayName,active"
            },
            "description": "A comma-separated list of attributes to return in the response. This query parameter supersedes excludedAttributes, so providing the same attribute(s) to both will result in the attribute(s) being returned."
          },
          {
            "in": "query",
            "name": "excludedAttributes",
            "schema": {
              "type": "string",
              "example": "displayName,active"
            },
            "description": "A comma-separated list of attributes to exclude from the response. *Some attributes cannot be excluded.*"
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "application/scim+json": {
              "schema": {
                "$ref": "#/components/schemas/accountPostRequest"
              }
            },
            "*/*": {
              "schema": {
                "$ref": "#/components/schemas/accountPostRequest"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Updates an existing Account and returns that Account.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/accountResponse"
                }
              }
            }
          }
        }
      },
      "delete": {
        "operationId": "deleteAccount",
        "tags": [
          "Accounts"
        ],
        "summary": "Deletes an existing Account.",
        "description": "The endpoint used to delete an Account resource. **This is not reversible.**",
        "parameters": [
          {
            "name": "accountId",
            "in": "path",
            "schema": {
              "type": "string",
              "example": "c7c7777c7ef77e77777ee77e7a1f0444"
            },
            "description": "The id of the Account.",
            "required": true
          }
        ],
        "responses": {
          "204": {
            "description": "Returns a 204 with no response body if delete was successful."
          }
        }
      }
    },
    "/Alerts": {
      "get": {
        "operationId": "getAlerts",
        "tags": [
          "Alerts"
        ],
        "summary": "Returns all Alert resources.",
        "description": "This endpoint returns all Alert resources. <br/><br/>\nAttributes to include in the response can be specified with the 'attributes' query parameter. <br/><br/> \nAttributes to exclude from the response can be specified with the 'excludedAttributes' query parameter. <br/><br/>\nThe schemas related to Alerts are:\n- **urn:ietf:params:scim:schemas:core:1.0:Alert**\n- **urn:ietf:params:scim:schemas:core:1.0:AlertInput**\n",
        "parameters": [
          {
            "in": "query",
            "name": "filter",
            "schema": {
              "type": "string",
              "example": "name eq \"0000000001\""
            },
            "description": "Allows for query filters according to RFC-7644, Section 3.4.2.2 - not all operations are supported."
          },
          {
            "in": "query",
            "name": "lookupByName",
            "schema": {
              "type": "boolean",
              "default": false,
              "example": true
            },
            "description": "Set to true if the Alert name is passed instead of the Alert id."
          },
          {
            "in": "query",
            "name": "sortBy",
            "schema": {
              "type": "string",
              "example": "name"
            },
            "description": "Allows sorting the results by a resource's attributes."
          },
          {
            "in": "query",
            "name": "sortOrder",
            "schema": {
              "type": "string",
              "default": "ascending"
            },
            "example": "descending",
            "description": "Determines what order to sort results in."
          },
          {
            "in": "query",
            "name": "startIndex",
            "schema": {
              "type": "integer",
              "example": "10",
              "default": 1
            },
            "description": "Determines the starting index of the result set."
          },
          {
            "in": "query",
            "name": "count",
            "schema": {
              "type": "integer",
              "example": "10",
              "default": 1000
            },
            "description": "Specifies the number of results per page."
          },
          {
            "in": "query",
            "name": "attributes",
            "schema": {
              "type": "string",
              "example": "name"
            },
            "description": "A comma-separated list of attributes to return in the response. This query parameter supersedes excludedAttributes, so providing the same attribute(s) to both will result in the attribute(s) being returned."
          },
          {
            "in": "query",
            "name": "excludedAttributes",
            "schema": {
              "type": "string",
              "example": "name, application"
            },
            "description": "A comma-separated list of attributes to exclude from the response. **Some attributes cannot be excluded.**"
          }
        ],
        "responses": {
          "200": {
            "description": "Returned all SCIM resources for this endpoint.",
            "content": {
              "application/json": {
                "schema": {
                  "properties": {
                    "totalResults": {
                      "description": "Number of resources returned for this endpoint.",
                      "type": "integer",
                      "example": "1,"
                    },
                    "startIndex": {
                      "description": "The starting index of the resource set list. Can be specified with startIndex query parameter.",
                      "type": "integer",
                      "example": 1
                    },
                    "schemas": {
                      "description": "SCIM Schema used for response.",
                      "type": "array",
                      "items": {
                        "type": "string"
                      },
                      "example": [
                        "urn:ietf:params:scim:api:messages:2.0:ListResponse"
                      ]
                    },
                    "Resources": {
                      "type": "array",
                      "items": {
                        "$ref": "#/components/schemas/alertGetResponse"
                      }
                    }
                  }
                }
              },
              "application/scim+json": {
                "schema": {
                  "properties": {
                    "totalResults": {
                      "description": "Number of Alert resources returned.",
                      "type": "integer",
                      "example": "1500,"
                    },
                    "startIndex": {
                      "description": "The starting index of the resource set list. Can be specified with startIndex query parameter.",
                      "type": "integer",
                      "example": 1
                    },
                    "schemas": {
                      "description": "SCIM Schema used for response.",
                      "type": "array",
                      "items": {
                        "type": "string"
                      },
                      "example": [
                        "urn:ietf:params:scim:api:messages:2.0:ListResponse"
                      ]
                    },
                    "Resources": {
                      "description": "The SCIM resources returned for this endpoint.",
                      "type": "array",
                      "items": {
                        "$ref": "#/components/schemas/alertGetResponse"
                      }
                    }
                  }
                }
              }
            }
          }
        }
      },
      "post": {
        "operationId": "createAlert",
        "tags": [
          "Alerts"
        ],
        "summary": "Creates a new Alert.",
        "description": "The endpoint used to create an Alert resource.",
        "parameters": [
          {
            "in": "query",
            "name": "attributes",
            "schema": {
              "type": "string",
              "example": "name"
            },
            "description": "A comma-separated list of attributes to return in the response. This query parameter supersedes excludedAttributes, so providing the same attribute(s) to both will result in the attribute(s) being returned."
          },
          {
            "in": "query",
            "name": "excludedAttributes",
            "schema": {
              "type": "string",
              "example": "name, application"
            },
            "description": "A comma-separated list of attributes to exclude from the response. **Some attributes cannot be excluded.**"
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "application/scim+json": {
              "schema": {
                "$ref": "#/components/schemas/alertPostRequestBody"
              }
            }
          }
        },
        "responses": {
          "201": {
            "description": "Creates an Alert and returns the resultant Alert.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/alertGetResponse"
                }
              }
            }
          }
        }
      }
    },
    "/Alerts/{alertId}": {
      "get": {
        "operationId": "getAlertById",
        "tags": [
          "Alerts"
        ],
        "summary": "Returns an Alert resource based on id.",
        "description": ">The Alert resource with matching id is returned.<br /><br />\n>Attributes to include in the response can be specified with the attributes query parameter. <br /><br /> \n>Attributes to exclude from the response can be specified with the excludedAttributes query parameter. <br /><br />\n",
        "parameters": [
          {
            "name": "alertId",
            "in": "path",
            "schema": {
              "type": "string",
              "example": "c0a8019c802d1e5a81802eb2b57e020f"
            },
            "description": "id of Alert resource.",
            "required": true
          },
          {
            "in": "query",
            "name": "attributes",
            "schema": {
              "type": "string",
              "example": "name"
            },
            "description": "A comma-separated list of attributes to return in the response. This query parameter supersedes excludedAttributes, so providing the same attribute(s) to both will result in the attribute(s) being returned."
          },
          {
            "in": "query",
            "name": "excludedAttributes",
            "schema": {
              "type": "string",
              "example": "name, application"
            },
            "description": "A comma-separated list of attributes to exclude from the response. **Some attributes cannot be excluded.**"
          }
        ],
        "responses": {
          "200": {
            "description": "Returns a single Alert resource based on the id.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/alertByIdGetBody"
                }
              }
            }
          }
        }
      }
    },
    "/Applications": {
      "get": {
        "operationId": "getApplications",
        "tags": [
          "Applications"
        ],
        "summary": "Returns all Application resources.",
        "description": "This endpoint returns all Application resources. <br /><br />\nAttributes to include in the response can be specified with the 'attributes' query parameter. <br /><br /> \nAttributes to exclude from the response can be specified with the 'excludedAttributes' query parameter. <br /><br />\nThe schema related to Applications is: \n- **urn:ietf:params:scim:schemas:core:1.0:Application**\n",
        "parameters": [
          {
            "in": "query",
            "name": "filter",
            "schema": {
              "type": "string",
              "example": "type eq \"Active Directory - Direct\""
            },
            "description": "Allows for query filters according to RFC-7644, Section 3.4.2.2 - not all operations are supported."
          },
          {
            "in": "query",
            "name": "lookupByName",
            "schema": {
              "type": "boolean",
              "default": false,
              "example": true
            }
          },
          {
            "in": "query",
            "name": "sortBy",
            "schema": {
              "type": "string",
              "example": "name"
            },
            "description": "Allows sorting the results by a resource's attributes."
          },
          {
            "in": "query",
            "name": "sortOrder",
            "schema": {
              "type": "string",
              "default": "ascending",
              "example": "descending"
            },
            "description": "Determines what order to sort results in."
          },
          {
            "in": "query",
            "name": "startIndex",
            "schema": {
              "type": "integer",
              "example": 10,
              "default": 1
            },
            "description": "Determines the starting index of the result set."
          },
          {
            "in": "query",
            "name": "count",
            "schema": {
              "type": "integer",
              "example": 10,
              "default": 1
            },
            "description": "Specifies the number of results per page."
          },
          {
            "in": "query",
            "name": "attributes",
            "schema": {
              "type": "string",
              "example": "name,type,features"
            },
            "description": "The Application attributes to include in the response. The query parameter value is a comma-separated list of fields to be returned in the response for each Application. The attributes listed will be the only ones returned in the response, with the exception of id, schemas, and meta, which are always returned for an Application."
          },
          {
            "in": "query",
            "name": "excludedAttributes",
            "schema": {
              "type": "string",
              "example": "features"
            },
            "description": "The Application attributes to exclude frome the response. The query parameter value is a comma-separated list of fields to be excluded from the response for each Application. The attributes listed will be the only ones excluded frome the response, with the exception of id, schemas, and meta, which are always returned for an Application."
          }
        ],
        "responses": {
          "200": {
            "description": "Returns all SCIM Application resources.",
            "content": {
              "application/json": {
                "schema": {
                  "properties": {
                    "totalResults": {
                      "description": "Number of Application resources returned.",
                      "type": "integer",
                      "example": "18,"
                    },
                    "startIndex": {
                      "description": "The starting index of the resource set list. Can be specified with startIndex query parameter.",
                      "type": "integer",
                      "example": 1
                    },
                    "schemas": {
                      "description": "SCIM Schema used for response.",
                      "type": "array",
                      "items": {
                        "type": "string"
                      },
                      "example": [
                        "urn:ietf:params:scim:api:messages:2.0:ListResponse"
                      ]
                    },
                    "Resources": {
                      "type": "array",
                      "items": {
                        "$ref": "#/components/schemas/applicationResponse"
                      }
                    }
                  }
                }
              },
              "application/scim+json": {
                "schema": {
                  "properties": {
                    "totalResults": {
                      "description": "Number of Application resources returned.",
                      "type": "integer",
                      "example": "1500,"
                    },
                    "startIndex": {
                      "description": "The starting index of the resource set list. Can be specified with startIndex query parameter.",
                      "type": "integer",
                      "example": 1
                    },
                    "schemas": {
                      "description": "SCIM Schema used for response.",
                      "type": "array",
                      "items": {
                        "type": "string"
                      },
                      "example": [
                        "urn:ietf:params:scim:api:messages:2.0:ListResponse"
                      ]
                    },
                    "Resources": {
                      "description": "The SCIM resources returned for this endpoint.",
                      "type": "array",
                      "items": {
                        "$ref": "#/components/schemas/applicationResponse"
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    },
    "/Applications/{applicationId}": {
      "get": {
        "operationId": "getApplication",
        "tags": [
          "Applications"
        ],
        "summary": "Returns an Application resource based on id or name.",
        "description": "The Application resource with matching id or name is returned. Attributes to include in the response can be specified with the 'attributes' query parameter. \nAttributes to exclude from the response can be specified with the 'excludedAttributes' query parameter. \nThe schema related to Application is: \n- **urn:ietf:params:scim:schemas:sailpoint:1.0:Application.**\n",
        "parameters": [
          {
            "name": "applicationId",
            "in": "path",
            "schema": {
              "type": "string",
              "example": "7f00000180281df7818028bfaae8035b"
            },
            "description": "id or name of Application resource.",
            "required": true
          },
          {
            "in": "query",
            "name": "attributes",
            "schema": {
              "type": "string",
              "example": "name,type,features"
            },
            "description": "The Application attributes to include in the response. The query parameter value is a comma-separated list of fields to be returned in the response for each Application. The attributes listed will be the only ones returned in the response, with the exception of id, schemas, and meta, which are always returned for an Application."
          },
          {
            "in": "query",
            "name": "excludedAttributes",
            "schema": {
              "type": "string",
              "example": "features"
            },
            "description": "The Application attributes to exclude frome the response. The query parameter value is a comma-separated list of fields to be excluded from the response for each Application. The attributes listed will be the only ones excluded frome the response, with the exception of id, schemas, and meta, which are always returned for an Application."
          }
        ],
        "responses": {
