                      "permissions": {
                        "type": "object",
                        "properties": {
                          "can_download": {
                            "type": "boolean",
                            "example": true,
                            "description": "If the shared link allows for downloading of files.\nThis can only be set when `access` is set to\n`open` or `company`."
