            "allOf": [
              { "$ref": "#/components/schemas/H264Profile" },
              { "xml": { "name": "profile" }, "description": "H.264 Profile." }
            ]
          },
          "QualityLevel": {
            "allOf": [
              { "$ref": "#/components/schemas/H264QualityLevel" },
              {
                "xml": { "name": "qualityLevel" },
                "description": "Leave as STANDARD_QUALITY or choose a different value (which might result in additional costs to run the channel).\n- ENHANCED_QUALITY: Produces a slightly better video quality without an increase in the bitrate. Has an effect only when the Rate control mode is QVBR or CBR. If this channel is in a MediaLive multiplex, the value must be ENHANCED_QUALITY.\n- STANDARD_QUALITY: Valid for any Rate control mode."
              }
            ]
          },
          "QvbrQualityLevel": {
            "allOf": [
              { "$ref": "#/components/schemas/__integerMin1Max10" },
              {
                "xml": { "name": "qvbrQualityLevel" },
                "description": "Controls the target quality for the video encode. Applies only when the rate control mode is QVBR. Set values for the QVBR quality level field and Max bitrate field that suit your most important viewing devices. Recommended values are:\n- Primary screen: Quality level: 8 to 10. Max bitrate: 4M\n- PC or tablet: Quality level: 7. Max bitrate: 1.5M to 3M\n- Smartphone: Quality level: 6. Max bitrate: 1M to 1.5M"
              }
            ]
          },
          "RateControlMode": {
            "allOf": [
              { "$ref": "#/components/schemas/H264RateControlMode" },
              {
                "xml": { "name": "rateControlMode" },
                "description": "Rate control mode.\n\nQVBR: Quality will match the specified quality level except when it is constrained by the\nmaximum bitrate.  Recommended if you or your viewers pay for bandwidth.\n\nVBR: Quality and bitrate vary, depending on the video complexity. Recommended instead of QVBR\nif you want to maintain a specific average bitrate over the duration of the channel.\n\nCBR: Quality varies, depending on the video complexity. Recommended only if you distribute\nyour assets to devices that cannot handle variable bitrates.\n\nMultiplex: This rate control mode is only supported (and is required) when the video is being\ndelivered to a MediaLive Multiplex in which case the rate control configuration is controlled\nby the properties within the Multiplex Program."
              }
            ]
          },
          "ScanType": {
            "allOf": [
              { "$ref": "#/components/schemas/H264ScanType" },
              {
                "xml": { "name": "scanType" },
                "description": "Sets the scan type of the output to progressive or top-field-first interlaced."
              }
            ]
          },
          "SceneChangeDetect": {
            "allOf": [
              { "$ref": "#/components/schemas/H264SceneChangeDetect" },
              {
                "xml": { "name": "sceneChangeDetect" },
                "description": "Scene change detection.\n\n- On: inserts I-frames when scene change is detected.\n- Off: does not force an I-frame when scene change is detected."
              }
            ]
          },
          "Slices": {
            "allOf": [
              { "$ref": "#/components/schemas/__integerMin1Max32" },
              {
                "xml": { "name": "slices" },
                "description": "Number of slices per picture. Must be less than or equal to the number of macroblock rows for progressive pictures, and less than or equal to half the number of macroblock rows for interlaced pictures.\nThis field is optional; when no value is specified the encoder will choose the number of slices based on encode resolution."
              }
            ]
          },
          "Softness": {
            "allOf": [
              { "$ref": "#/components/schemas/__integerMin0Max128" },
              {
                "xml": { "name": "softness" },
                "description": "Softness. Selects quantizer matrix, larger values reduce high-frequency content in the encoded image."
              }
            ]
          },
          "SpatialAq": {
            "allOf": [
              { "$ref": "#/components/schemas/H264SpatialAq" },
              {
                "xml": { "name": "spatialAq" },
                "description": "If set to enabled, adjust quantization within each frame based on spatial variation of content complexity."
              }
            ]
          },
          "SubgopLength": {
            "allOf": [
              { "$ref": "#/components/schemas/H264SubGopLength" },
