          "TimecodeInsertion": {
            "allOf": [
              { "$ref": "#/components/schemas/H264TimecodeInsertionBehavior" },
              {
                "xml": { "name": "timecodeInsertion" },
                "description": "Determines how timecodes should be inserted into the video elementary stream.\n- 'disabled': Do not include timecodes\n- 'picTimingSei': Pass through picture timing SEI messages from the source specified in Timecode Config"
              }
