          "Name": {
            "allOf": [
              { "$ref": "#/components/schemas/__string" },
              {
                "xml": { "name": "name" },
                "description": "The name of this VideoDescription. Outputs will use this name to uniquely identify this Description.  Description names should be unique within this Live Event."
              }
            ]
          },
          "RespondToAfd": {
            "allOf": [
              { "$ref": "#/components/schemas/VideoDescriptionRespondToAfd" },
              {
                "xml": { "name": "respondToAfd" },
                "description": "Indicates how to respond to the AFD values in the input stream. RESPOND causes input video to be clipped, depending on the AFD value, input display aspect ratio, and output display aspect ratio, and (except for FRAME_CAPTURE codec) includes the values in the output. PASSTHROUGH (does not apply to FRAME_CAPTURE codec) ignores the AFD values and includes the values in the output, so input video is not clipped. NONE ignores the AFD values and does not include the values through to the output, so input video is not clipped."
              }
            ]
          },
          "ScalingBehavior": {
            "allOf": [
              {
                "$ref": "#/components/schemas/VideoDescriptionScalingBehavior"
              },
              {
                "xml": { "name": "scalingBehavior" },
                "description": "STRETCH_TO_OUTPUT configures the output position to stretch the video to the specified output resolution (height and width). This option will override any position value. DEFAULT may insert black boxes (pillar boxes or letter boxes) around the video to provide the specified output resolution."
              }
            ]
