openapi: 3.0.2
paths:
  tmp0:
    post:
      description: Allows a caller to add a convicted threat to either the Global
        Quarantine or Global Safe list for a Tenant.
      parameters:
      - description: The SHA256 Hash for the threat.
        example: ''
        in: query
        name: SHA256
        required: true
        schema: {type: string}
      - description: The list type to which the threat belongs.
        example: GlobalQuarantine or GlobalSafe
        in: query
        name: List Type
        required: true
        schema: {type: string}
      - description: The reason the file was added to the list.
        example: ''
        in: query
        name: Reason
        required: true
        schema: {type: string}
      - description: The field is only required for GlobalSafe list types.
        example: AdminTool, CommercialSoftware, Drivers, InternalApplication, OperatingSystem,
          SecuritySoftware, or None.
        in: query
        name: Category
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: Cylance Add to Global List
  tmp1:
    post:
      description: Creates a new User.
      parameters:
      - description: ''
        example: ''
        in: query
        name: Email
        required: true
        schema: {type: string}
      - description: ''
        example: ''
        in: query
        name: First Name
        required: true
        schema: {type: string}
      - description: ''
        example: ''
        in: query
        name: Last Name
        required: true
        schema: {type: string}
      - description: Zone ID or a comma separated list of Zone ID's, role type is
          required if zone ID is given.
        example: ''
        in: query
        name: Zone ID
        required: true
        schema: {type: string}
      - description: Zone Role Type or a comma separated list of Zone Role Type's,
          must have a role type for every Zone ID given.
        example: 'Zone Manager: 00000000-0000-0000-0000-000000000001, User: 00000000-0000-0000-0000-000000000002'
        in: query
        name: Zone Role Type
        required: true
        schema: {type: string}
      - description: Zone ID or a comma separated list of Zone ID's, role type is
          required if zone ID is given.
        example: 'User: 00000000-0000-0000-0000-000000000001, Administrator: 00000000-0000-0000-0000-000000000002,
          Zone Manager: 00000000-0000-0000-0000-000000000003'
        in: query
        name: User Role
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: Cylance Create User
  tmp10:
    post:
      description: Retrieves information about the threats discovered on a device.
        Returns duplicate if a threat has been cleared.
      parameters:
      - description: Unique ID of device to be searched for.
        example: ''
        in: query
        name: Device ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp10'}
          description: Successful request
      summary: Cylance Get Device Threats
  tmp11:
    post:
      description: Gets all zones for a given device.
      parameters:
      - description: Unique ID of device to be searched for.
        example: ''
        in: query
        name: Device ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp11'}
          description: Successful request
      summary: Cylance Get Device Zones
  tmp12:
    post:
      description: Gets all devices for a given Tenant
      parameters: []
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp12'}
          description: Successful request
      summary: Cylance Get Devices
  tmp13:
    post:
      description: Get a list of global list resources for a tenant, sorted by date.
      parameters:
      - description: The type of the list to retrieve hashes for.
        example: 0=GlobalQuarantined, 1=GlobalSafe
        in: query
        name: List Type ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp13'}
          description: Successful request
      summary: Cylance Get Global List
  tmp14:
    post:
      description: Gets all policies for a given Tenant
      parameters: []
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp14'}
          description: Successful request
      summary: Cylance Get Policies
  tmp15:
    post:
      description: Retrieves specific information about a policy
      parameters:
      - description: Unique ID of the policy to be searched for.
        example: ''
        in: query
        name: Policy ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp15'}
          description: Successful request
      summary: Cylance Get Policy
  tmp16:
    post:
      description: Retrieves specific information about a threat, given the SHA256
        code of the threat.
      parameters:
      - description: Unique ID of threat to be searched for, can be found in the threat
          protection section.
        example: ''
        in: query
        name: SHA256
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp16'}
          description: Successful request
      summary: Cylance Get Threat
  tmp17:
    post:
      description: Retrieves which devices contain the specified threat.
      parameters:
      - description: Unique ID of threat to be searched for, can be found in the threat
          protection section.
        example: ''
        in: query
        name: SHA256
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp17'}
          description: Successful request
      summary: Cylance Get Threat Devices
  tmp18:
    post:
      description: Retrieves a download link for a given threat.
      parameters:
      - description: Unique ID of threat to be searched for, can be found in the threat
          protection section.
        example: ''
        in: query
        name: SHA256
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp18'}
          description: Successful request
      summary: Cylance Get Threat Download URL
  tmp19:
    post:
      description: Retrieves a list of Console threat resources belonging to a Tenant.
      parameters: []
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp19'}
          description: Successful request
      summary: Cylance Get Threats
  tmp2:
    post:
      description: Creates a new Zone based on the name and policy ID specified by
        the user.
      parameters:
      - description: The value of the zone - Low, Normal, or High
        example: ''
        in: query
        name: Criticality
        required: true
        schema: {type: string}
      - description: Name of the Zone
        example: ''
        in: query
        name: Name
        required: true
        schema: {type: string}
      - description: The unique ID for the policy assigned to the zone.
        example: ''
        in: query
        name: Policy ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp2'}
          description: Successful request
      summary: Cylance Create Zone
  tmp20:
    post:
      description: Retrieves information about a user.
      parameters:
      - description: The email of the user. Primary method of search.
        example: ''
        in: query
        name: Email
        required: true
        schema: {type: string}
      - description: User ID to be searched for - back up option if user email is
          not known.
        example: ''
        in: query
        name: User ID
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp20'}
          description: Successful request
      summary: Cylance Get User
  tmp21:
    post:
      description: Retrieves a list of users belonging to a tenant.
      parameters: []
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp21'}
          description: Successful request
      summary: Cylance Get Users
  tmp22:
    post:
      description: Gets Zone information by Zone ID
      parameters:
      - description: Unique ID of zone to be searched for.
        example: ''
        in: query
        name: Zone ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp22'}
          description: Successful request
      summary: Cylance Get Zone
  tmp23:
    post:
      description: Gets all devices for a given zone.
      parameters:
      - description: Unique Zone ID of zone to check. Zone ID is found at the end
          of the Zone URL.
        example: ''
        in: query
        name: Zone ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp23'}
          description: Successful request
      summary: Cylance Get Zone Devices
  tmp24:
    post:
      description: Gets all zones for a given Tenant
      parameters: []
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp24'}
          description: Successful request
      summary: Cylance Get Zones
  tmp25:
    post:
      description: Updates specified information about a device
      parameters:
      - description: Unique ID of device to be searched for.
        example: ''
        in: query
        name: Device ID
        required: true
        schema: {type: string}
      - description: The name of the device.
        example: ''
        in: query
        name: Device Name
        required: true
        schema: {type: string}
      - description: ID of the Policy assigned to the device.
        example: ''
        in: query
        name: Policy ID
        required: true
        schema: {type: string}
      - description: The comma separated list of zone identifiers which the device
          is to be assigned. Zone ID's can be retrieved using the API or by looking
          at the end of a URL for a zone.
        example: ''
        in: query
        name: Add Zone ID
        required: false
        schema: {type: string}
      - description: The comma separated list of zone ID's from which the device is
          to be removed. Zone ID's can be found on the end of the url for a zone.
        example: ''
        in: query
        name: Remove Zone ID
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp25'}
          description: Successful request
      summary: Cylance Update Device
  tmp26:
    post:
      description: Updates information about device threat.
      parameters:
      - description: Unique ID of device to be searched for.
        example: ''
        in: query
        name: Device ID
        required: true
        schema: {type: string}
      - description: The SHA256 hash of the convicted threat.
        example: ''
        in: query
        name: Threat ID
        required: true
        schema: {type: string}
      - description: The requested status update for the convicted threat.
        example: 'Options are: Quarantine and Waive.'
        in: query
        name: Event
        required: true
        schema: {type: string}
      responses:
