{
    "openapi": "3.0.0",
    "info": {
        "title": "Policies System API",
        "description": "In the context of the Promotion Alerts, this API will create promotion alarms when selling products with undesired prices and promotions. It will create conditions that will check if the prices and the promotions are correct. If not, the system will alarm the account with information about the product sold at unexpected prices.",
        "contact": {},
        "version": ""
    },
    "servers": [
        {
            "url": "https://{accountName}.{environment}.com.br",
            "description": "VTEX server url",
            "variables": {
                "accountName": {
                    "description": "Name of the VTEX account. Used as part of the URL",
                    "default": "{accountName}"
                },
                "environment": {
                    "description": "Environment to use. Used as part of the URL.",
                    "default": "{environment}"
                }
            }
        }
    ],
    "paths": {
        "/api/policy-engine/policies": {
            "get": {
                "tags": [
                    "Policy"
                ],
                "summary": "Get Policy List",
                "description": "Retrieves a list of all policies in the account and general information of each policy.",
                "operationId": "Policy_List",
                "parameters": [
                    {
                        "name": "Content-Type",
                        "in": "header",
                        "description": "Describes the type of the content being sent",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    },
                    {
                        "name": "Accept",
                        "in": "header",
                        "description": "HTTP Client Negotiation _Accept_ Header. Indicates the types of responses the client can understand ",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "",
                        "headers": {},
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "array",
                                    "items": {
                                        "$ref": "#/components/schemas/PolicyGetResponse"
                                    },
                                    "example": [
                                        {
                                            "id": "pa_test_001",
                                            "name": "TestAlarmBerenice",
                                            "description": "TesteMarcosPromotionsAlert",
                                            "statements": [
                                                {
                                                    "effect": "Allow",
                                                    "actions": [
                                                        {
                                                            "id": "SendSlackMessage",
                                                            "metadata": {
                                                                "channel": "C01NJFF35R6",
                                                                "relatedUsers": [
                                                                    "URUNDC2NB"
                                                                ],
                                                                "alertDescription": "Avoid selling products from Berenice with a discount greater than 70%."
                                                            }
                                                        }
                                                    ],
                                                    "resource": "vrn:vtex.promotions-alert:aws-us-east-1:kamila:master:/_v/promotions_alert",
                                                    "condition": {
                                                        "conditions": [
                                                            {
                                                                "conditions": [],
                                                                "operation": "stringEquals",
                                                                "key": "brandId",
                                                                "values": [
                                                                    "2000001"
                                                                ]
                                                            },
                                                            {
                                                                "conditions": [],
                                                                "operation": "numericGreaterThan",
                                                                "key": "discountPercentage",
                                                                "values": [
                                                                    "70.00"
                                                                ]
                                                            }
                                                        ],
                                                        "operation": "and"
                                                    }
                                                }
                                            ]
                                        }
                                    ]
                                }
                            }
                        }
                    }
                }
            }
        },
