                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    }
                ],
                "requestBody": {
                    "description": "",
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/EvaluatePolicyRequest"
                            },
                            "example": {
                                "resource": "vrn:vtex.promotions-alert:aws-us-east-1:kamila:master:/_v/promotions_alert",
                                "context": {
                                    "brandId": "2000001",
                                    "discountPercentage": "91.00"
                                }
                            }
                        }
                    },
                    "required": true
                },
                "responses": {
                    "200": {
                        "description": "",
                        "headers": {},
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "array",
                                    "items": {
                                        "$ref": "#/components/schemas/PolicyActionGetResponse"
                                    },
                                    "example": [
                                        {
                                            "id": "SendSlackMessage",
                                            "metadata": {
                                                "channel": "C01NJFF35R6",
                                                "relatedUsers": [
                                                    "URUNDC2NB"
                                                ],
                                                "alertDescription": "Avoid selling products from Berenice with a discount greater than 70%."
                                            }
                                        }
                                    ]
                                }
                            }
                        }
                    }
                },
                "deprecated": false
            }
        },
