    "/assessments": {
      "post": {
        "operationId": "CreateAssessment",
        "description": " Creates an assessment in Audit Manager. ",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/CreateAssessmentResponse"
                }
              }
            }
          },
