{
	"openapi": "3.0.1",
	"servers": [
		{
			"description": "API para reserva de produtos do varejo",
			"url": "{{host}}/api/retail/v1",
			"variables": {
				"serverUrl": {
					"default": "localhost"
				},
				"serverHttpPort": {
					"default": "8080"
				}
			}
		}
	],
	"info": {
		"description": "API para reserva de produtos do varejo",
		"version": "1.000",
		"title": "Reservas de Produto",
		"contact": {
			"name": "T-Talk",
			"url": "api.Totvs.com.br",
			"email": "comiteintegracao@totvs.com.br"
		},
		"x-totvs": {
			"messageDocumentation": {
				"name": "ItemReserve",
				"description": "reserva",
				"segment": "Varejo"
			},
			"productInformation": [
				{
					"product": "Protheus",
					"contact": "ricardo.msousa@totvs.com.br",
					"description": "Inclusão de reserva de produto",
					"adapter": "LOJI704O.prw"
				}
			]
		}
	},
	"paths": {
		"/ItemReserves": {
			"get": {
				"tags": [
					"ItemReserve"
				],
				"summary": "Retorna todas as reservas de produtos",
				"x-totvs": {
					"productInformation": [
						{
							"product": "Protheus",
							"available": true,
							"note": "Este verbo está disponível com os parâmetros Authorization, Order, Page, PageSize, Fields",
							"minimalVersion": "12.1.21"
						}
					]
				},
				"description": "Retorna todas as reservas de produtos",
				"operationId": "getItemReserve",
				"parameters": [
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Order"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Page"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/PageSize"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Fields"
					}
				],
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/ItemReserve_2_000.json#/definitions/PagedItemReserves"
								}
							}
						}
					},
					"400": {
						"description": "Erro na requisição!",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			},
			"post": {
				"tags": [
					"itemReserve"
				],
				"summary": "Inclui reserva passada na requisição",
				"description": "Inclui a entidade ItemReserve passada na requisição",
				"operationId": "postItemReserve",
				"x-totvs": {
					"productInformation": [
						{
							"product": "Protheus",
							"available": true,
							"note": "Este verbo está disponível com os parâmetros Authorization e Fields",
							"minimalVersion": "12.1.21"
						}
					]
				},
				"parameters": [
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"requestBody": {
					"content": {
						"application/json": {
							"schema": {
								"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/ItemReserve_2_000.json#/definitions/ItemReserveType"
							}
						}
					},
					"description": "Reserva para ser incluida"
				},
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/ItemReserve_2_000.json#/definitions/ItemReserveType"
								}
							}
						}
					},
					"400": {
						"description": "erro na Inclusão!",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			}
		},
		"/ItemReserves/{InternalId}": {
			"get": {
				"tags": [
					"ItemReserve"
				],
				"summary": "Buscar reserva por InternalId que é constituido dos valores de documento da reserva e filial  D MG 01|01",
				"description": "Retorna os dados da reserva de acordo com o número do documento da reserva e filial passadas por parametro",
				"operationId": "getInternalId",
				"x-totvs": {
					"productInformation": [
						{
							"product": "Protheus",
							"available": true,
							"note": "Este verbo está disponível com os parâmetros InternalId, Authorization",
							"minimalVersion": "12.1.21"
						}
					]
				},
				"parameters": [
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					},
					{
						"$ref": "#/components/parameters/InternalId"
					}
				],
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso!",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/ItemReserve_2_000.json#/definitions/ItemReserveType"
								}
							}
						}
					},
					"400": {
						"description": "Erro na requisição!",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					},
					"404": {
						"description": "Reserva não localizada na base!",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			},
			"put": {
				"tags": [
					"itemReserve"
				],
				"summary": "Atualiza uma reserva passada na requisição",
				"description": "Atualiza a entidade ItemReserve passada na requisição",
				"operationId": "putitemReserve",
				"x-totvs": {
					"productInformation": [
						{
							"product": "Protheus",
							"available": true,
							"note": "Este verbo está disponível com os parâmetros Authorization ",
							"minimalVersion": "12.1.21"
						}
					]
				},
				"parameters": [
					{
						"$ref": "#/components/parameters/InternalId"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"requestBody": {
					"description": "Reserva para ser atualizada",
					"content": {
						"application/json": {
							"schema": {
								"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/ItemReserve_2_000.json#/definitions/ItemReserveType"
							}
						}
					}
				},
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/ItemReserve_2_000.json#/definitions/ItemReserveType"
								}
							}
						}
					},
					"400": {
						"description": "erro na Inclusão!",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					},
					"404": {
						"description": "Reserva não encontrada",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			},
			"delete": {
				"tags": [
					"ItemReserve"
				],
				"summary": "Deleta reserva passada na requisição",
				"description": "Deleta a entidade ItemReserve passada na requisição",
				"operationId": "deleteInternalId",
				"x-totvs": {
					"productInformation": [
						{
							"product": "Protheus",
							"available": true,
							"note": "Este verbo está disponível com os parâmetros Authorization",
							"minimalVersion": "12.1.21"
						}
					]
				},
				"parameters": [
					{
						"$ref": "#/components/parameters/InternalId"
					}
				],
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/ItemReserve_2_000.json#/definitions/ItemReserveType"
								}
							}
						}
					},
					"400": {
						"description": "erro ao deletar a reserva!",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					},
					"404": {
						"description": "Reserva não encontrada",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			}
		}
	},
	"components": {
		"parameters": {
			"InternalId": {
				"name": "InternalId",
				"in": "path",
				"required": true,
				"description": "Identificador único da reserva",
				"schema": {
					"type": "string"
				}
			}
		},
		"schemas": {}
	}
}
