      "RestoreTableFromBackupInput": {
        "type": "object",
        "required": ["TargetTableName", "BackupArn"],
        "title": "RestoreTableFromBackupInput",
        "properties": {
          "TargetTableName": {
            "allOf": [
              { "$ref": "#/components/schemas/TableName" },
