              { "$ref": "#/components/schemas/BillingMode" },
              { "description": "The billing mode of the restored table." }
            ]
          },
          "GlobalSecondaryIndexOverride": {
            "allOf": [
              { "$ref": "#/components/schemas/GlobalSecondaryIndexList" },
              {
