  contact: {email: frikky@shuffler.io, name: '@frikkylikeme', url: 'https://twitter.com/frikkylikeme'}
  description: Automated generation of Micro Focus ArcSight ESM
  title: Micro Focus ArcSight ESM
  version: 1.0.0
  x-logo: data:image/png;base64,
openapi: 3.0.2
paths:
  tmp0:
    post:
      description: Download a report from Arcsight
      parameters:
      - description: ID of the report to download
        example: 9Zh0OAW8BABCWnCdbCouvaw==
        in: query
        name: Report ID
        required: true
        schema: {type: string}
      - description: Must be one of pdf, csv, html, xls, or rtf. Must be the same
          as the report type in Arcsight, otherwise it will mistype the data
        example: pdf
        in: query
        name: Extension Type
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: ArcSightESM Download Report
  tmp1:
    post:
      description: Get all cases from ArcSight ESM
      parameters: []
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: ArcSightESM Get All Case IDs
  tmp2:
    post:
      description: Get case details from ArcSight ESM
      parameters:
      - description: Identifier for the case you want info from
        example: 7mPKk1FwBABDEJieUmD7nGQ==
        in: query
        name: Case ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp2'}
          description: Successful request
      summary: ArcSightESM Get Case Details
  tmp3:
    post:
      description: Get security events from ArcSight ESM
      parameters:
      - description: Identifier for the event.
        example: '229593'
        in: query
        name: Event ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp3'}
          description: Successful request
      summary: ArcSightESM Get Security Events
  tmp4:
    post:
      description: Set annotation stage for ArcSight ESM
      parameters:
      - description: Identifier for the event.
        example: '229593'
        in: query
        name: Event ID
        required: true
        schema: {type: string}
      - description: Identifier for the user.
        example: 1eh+LHGcBABCADpqTK1Xp2Q==
        in: query
        name: User ID
        required: true
        schema: {type: string}
      - description: Identifier for the stage.
        example: RN0kiNvoAABCAAQXv8J8ORQ==
        in: query
        name: Stage ID
        required: true
        schema: {type: string}
      - description: A comment describing the change.
        example: ''
        in: query
        name: Comment
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp4'}
          description: Successful request
      summary: ArcSightESM Set Annotation Stage
  tmp5:
    post:
      description: Update a case in ArcSight ESM
      parameters:
      - description: ID of the case
        example: 77kXfDmcBABCADmU8Go28RA==
        in: query
        name: Case ID
        required: true
        schema: {type: string}
      - description: ArcSight ESM URI of the case
        example: ''
        in: query
        name: URI of Case
        required: false
        schema: {type: string}
      - description: Name of Case
        example: ''
        in: query
        name: Name
        required: false
        schema: {type: string}
      - description: Action for the case
        example: ''
        in: query
        name: Action
        required: false
        schema: {type: string}
      - description: Type of agent the attack was
        example: ''
        in: query
