info:
  contact: {email: frikky@shuffler.io, name: '@frikkylikeme', url: 'https://twitter.com/frikkylikeme'}
  description: Automated generation of Symantec Advanced Threat Protection
  title: Symantec Advanced Threat Protection
  version: 1.0.0
  x-logo: data:image/png;base64,
openapi: 3.0.2
paths:
  tmp0:
    post:
      description: Cancel an isolate, rejoin, or delete file command that is in progress.
      parameters:
      - description: ID of Symantec Advanced Threat Protection Command
        example: d242dd6d-d4ab-4207-9581-d923e56fd121
        in: query
        name: Command ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: Symantec Advanced Threat Protection Cancel Command
  tmp1:
    post:
      description: Delete all instances of that file on the specified endpoint.
      parameters:
      - description: Device ID of the target computer
        example: cb46d251-151d-4583-a8fb-ebff7c42cfd8
        in: query
        name: Device UID
        required: true
        schema: {type: string}
      - description: SHA256 hash of file to delete
        example: 8692251329fef60490be1c26281710b7e88250fd82b3f679f87d6785db854ed5
        in: query
        name: File Hash
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: Symantec Advanced Threat Protection Delete File
  tmp2:
    post:
      description: Cut off the connections that the endpoint has to internal networks
        and external networks.
      parameters:
      - description: ID of endpoint to isolate
        example: cb46d251-151d-4583-a8fb-ebff7c42cfd8
        in: query
        name: Target
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp2'}
          description: Successful request
      summary: Symantec Advanced Threat Protection Isolate Endpoint
  tmp3:
    post:
      description: "Re-establish that endpoint\u2019s connections to internal networks\
        \ and external networks. You can only rejoin endpoints that are isolated."
      parameters:
      - description: ID of endpoint to rejoin
        example: cb46d251-151d-4583-a8fb-ebff7c42cfd8
        in: query
        name: Target
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp3'}
          description: Successful request
      summary: Symantec Advanced Threat Protection Rejoin Endpoint
  tmp4:
    post:
      description: Get ATP appliance information.
      parameters: []
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp4'}
          description: Successful request
      summary: Symantec Advanced Threat Protection Get Appliances
  tmp5:
    post:
      description: Get events from ATP.
      parameters:
      - description: The search condition is expressed in the form of a predicate,
          which consists of terms and other components (for example, logical operators
          and special characters that specify exactly which events to retrieve). See
          Readme for more information.
        example: log_time >= '2017-01-01T00:00:00.000Z' and log_time < '2017-01-08T00:00:00.000Z'
          and ( type_id=4096 or type_id=4123)
        in: query
        name: Search Condition(s)
        required: false
        schema: {type: string}
      - description: Specifies the beginning of the search time frame. See Readme
          for more information.
        example: '2017-01-01T00:00:00.000Z'
        in: query
        name: Start Time
        required: false
        schema: {type: string}
      - description: Specifies the end of the search time frame. See Readme for more
          information.
        example: '2017-01-08T00:00:00.000Z'
        in: query
        name: End Time
        required: false
        schema: {type: string}
      - description: Specifies the maximum number of events to return. Maximum is
          1000.
        example: '100'
        in: body
        name: Limit
        required: false
        schema: {type: string}
      - description: Used for events cursoring. The value of the next field must be
          treated as an opaque value. Use this value (data and type) in the next query.
        example: ZXhhbXBsZSBuZXh0IHN0cmluZw==
        in: query
        name: Next
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp5'}
          description: Successful request
      summary: Symantec Advanced Threat Protection Get Events
  tmp6:
    post:
      description: Get file details from ATP.
      parameters:
      - description: SHA256 or MD5 file hash
        example: 2e7d2c03a9507ae265ecf5b5356885a53393a2029d241394997265a1a25aefc6
        in: query
        name: File Hash
        required: true
        schema: {type: string}
      - description: Either 'sha256' or 'md5'.
        example: sha256
        in: query
        name: Hash Type
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp6'}
          description: Successful request
      summary: Symantec Advanced Threat Protection Get File Details
  tmp7:
    post:
      description: Get from ATP events that are related to incidents.
      parameters:
      - description: The search condition is expressed in the form of a predicate,
          which consists of terms and other components (for example, logical operators
          and special characters that specify exactly which events to retrieve). See
          Readme for more information.
        example: log_time >= '2017-01-01T00:00:00.000Z' and log_time < '2017-01-08T00:00:00.000Z'
