        example: ZXhhbXBsZSBuZXh0IHN0cmluZw==
        in: query
        name: Next
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp7'}
          description: Successful request
      summary: Symantec Advanced Threat Protection Get Incident Related Events
  tmp8:
    post:
      description: Get incidents from ATP.
      parameters:
      - description: The search condition is expressed in the form of a predicate,
          which consists of terms and other components (for example, logical operators
          and special characters that specify exactly which incidents to retrieve).
          See Readme for more information.
        example: updated >= '2017-01-01T00:00:00.000Z' and updated < '2017-01-08T00:00:00.000Z'
        in: query
        name: Search Condition(s)
        required: false
        schema: {type: string}
      - description: Specifies the beginning of the search time frame. See Readme
