        example: '2017-01-01T00:00:00.000Z'
        in: query
        name: Start Time
        required: false
        schema: {type: string}
      - description: Specifies the end of the search time frame. See Readme for more
          information.
        example: '2017-01-08T00:00:00.000Z'
        in: query
        name: End Time
        required: false
        schema: {type: string}
      - description: Specifies the maximum number of incidents to return. Maximum
          is 1000.
        example: '20'
        in: body
        name: Limit
        required: false
        schema: {type: string}
      - description: Used for incidents cursoring. The value of the next field must
          be treated as an opaque value. Use this value (data and type) in the next
          query.
        example: ZXhhbXBsZSBuZXh0IHN0cmluZw==
        in: query
        name: Next
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp8'}
          description: Successful request
      summary: Symantec Advanced Threat Protection Get Incidents
  tmp9:
    post:
      description: Query command state.
      parameters:
      - description: ID of Symantec Advanced Threat Protection Command
        example: d242dd6d-d4ab-4207-9581-d923e56fd121
        in: query
        name: Command ID
        required: true
        schema: {type: string}
      responses:
        '200':
