              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/dbv0.0.38_errors"
                }
              },
              "application/x-yaml": {
                "schema": {
                  "$ref": "#/components/schemas/dbv0.0.38_errors"
                }
              }
            }
          }
        }
      }
