                    "application/json": {
                      "schema": {
                        "$ref": "#/components/schemas/CostTypePagination"
                      }
                    }
                  }
                }
              }
            }
        },
        "/cost-models/": {
            "get": {
                "tags": [
                    "Cost Models"
                ],
                "summary": "List the cost models",
                "operationId": "listCostModels",
                "parameters": [{
                        "$ref": "#/components/parameters/QueryOffset"
                    },
                    {
                        "$ref": "#/components/parameters/QueryLimit"
                    },
                    {
                        "name": "source_uuid",
                        "required": false,
                        "in": "query",
                        "description": "Filter response on source uuid.",
                        "schema": {
                            "type": "string",
                            "format": "uuid"
                        }
                    },
                    {
                        "name": "source_type",
                        "required": false,
                        "in": "query",
                        "description": "Filter response on source type.",
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "name",
                        "required": false,
                        "in": "query",
                        "description": "Filter response on cost model name.",
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "currency",
                        "required": false,
                        "in": "query",
                        "description": "Filter response on currency.",
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "description",
                        "required": false,
                        "in": "query",
                        "description": "Filter response on cost model description.",
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "ordering",
                        "required": false,
                        "in": "query",
                        "description": "Order response on cost model by allowed fields.",
                        "schema": {
                            "type": "string",
                            "enum": [
                                "name",
                                "-name",
                                "source_type",
                                "-source_type",
                                "updated_timestamp",
                                "-updated_timestamp"
                            ]
                        }
                    }
                ],
                "security": [{
                    "basic_auth": []
                }],
                "responses": {
                    "200": {
                        "description": "A paginated list of cost model objects",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/CostModelPagination"
                                }
                            }
                        }
                    },
                    "401": {
                        "description": "Unauthorized"
                    },
                    "500": {
                        "description": "Unexpected Error",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/Error"
                                }
                            }
                        }
                    }
                }
            },
            "post": {
                "tags": [
                    "Cost Models"
                ],
                "summary": "Create a new cost model.",
                "operationId": "createCostModel",
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/CostModel"
                            }
                        }
