{
  "openapi": "3.0.2",
  "info": {
    "title": "simcore-service-catalog",
    "description": " Manages and maintains a catalog of all published components (e.g. macro-algorithms, scripts, etc)",
    "version": "0.3.2"
  },
  "paths": {
    "/v0/meta": {
      "get": {
        "tags": [
          "meta"
        ],
        "summary": "Get Service Metadata",
        "operationId": "get_service_metadata_v0_meta_get",
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Meta"
                }
              }
            }
          }
        }
      }
    },
    "/v0/dags": {
      "get": {
        "tags": [
          "DAG"
        ],
        "summary": "List Dags",
        "operationId": "list_dags_v0_dags_get",
        "parameters": [
          {
            "description": "Requests a specific page of the list results",
            "required": false,
            "schema": {
              "title": "Page Token",
              "type": "string",
              "description": "Requests a specific page of the list results"
            },
            "name": "page_token",
            "in": "query"
          },
          {
            "description": "Maximum number of results to be returned by the server",
            "required": false,
            "schema": {
              "title": "Page Size",
              "minimum": 0.0,
              "type": "integer",
              "description": "Maximum number of results to be returned by the server",
              "default": 0
            },
            "name": "page_size",
            "in": "query"
          },
          {
            "description": "Sorts in ascending order comma-separated fields",
            "required": false,
            "schema": {
              "title": "Order By",
              "type": "string",
              "description": "Sorts in ascending order comma-separated fields"
            },
            "name": "order_by",
            "in": "query"
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "title": "Response List Dags V0 Dags Get",
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/DAGOut"
                  }
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      },
      "post": {
        "tags": [
          "DAG"
        ],
        "summary": "Create Dag",
        "operationId": "create_dag_v0_dags_post",
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/DAGIn"
              }
            }
          },
          "required": true
        },
        "responses": {
          "201": {
            "description": "Successfully created",
            "content": {
              "application/json": {
                "schema": {
                  "title": "Response Create Dag V0 Dags Post",
                  "type": "integer"
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      }
    },
    "/v0/dags:batchGet": {
      "get": {
        "tags": [
          "DAG"
        ],
        "summary": "Batch Get Dags",
        "operationId": "batch_get_dags_v0_dags_batchGet_get",
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {}
              }
            }
          }
        }
      }
    },
    "/v0/dags:search": {
      "get": {
        "tags": [
          "DAG"
        ],
        "summary": "Search Dags",
        "operationId": "search_dags_v0_dags_search_get",
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {}
              }
            }
          }
        }
      }
    },
    "/v0/dags/{dag_id}": {
      "get": {
        "tags": [
          "DAG"
        ],
        "summary": "Get Dag",
        "operationId": "get_dag_v0_dags__dag_id__get",
        "parameters": [
          {
            "required": true,
            "schema": {
              "title": "Dag Id",
              "type": "integer"
            },
            "name": "dag_id",
            "in": "path"
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/DAGOut"
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      },
      "put": {
        "tags": [
          "DAG"
        ],
        "summary": "Replace Dag",
        "operationId": "replace_dag_v0_dags__dag_id__put",
        "parameters": [
          {
            "required": true,
            "schema": {
              "title": "Dag Id",
              "type": "integer"
            },
            "name": "dag_id",
            "in": "path"
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/DAGIn"
              }
            }
          },
          "required": true
        },
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/DAGOut"
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      },
      "delete": {
        "tags": [
          "DAG"
        ],
        "summary": "Delete Dag",
        "operationId": "delete_dag_v0_dags__dag_id__delete",
        "parameters": [
          {
            "required": true,
            "schema": {
              "title": "Dag Id",
              "type": "integer"
            },
            "name": "dag_id",
            "in": "path"
          }
        ],
        "responses": {
          "204": {
            "description": "Successfully deleted"
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      },
      "patch": {
        "tags": [
          "DAG"
        ],
        "summary": "Udpate Dag",
        "operationId": "udpate_dag_v0_dags__dag_id__patch",
        "parameters": [
          {
            "required": true,
            "schema": {
              "title": "Dag Id",
              "type": "integer"
            },
            "name": "dag_id",
            "in": "path"
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/DAGIn"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/DAGOut"
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      }
    },
    "/v0/services": {
      "get": {
        "tags": [
          "services"
        ],
        "summary": "List Services",
        "operationId": "list_services_v0_services_get",
        "parameters": [
          {
            "required": true,
            "schema": {
              "title": "User Id",
              "exclusiveMinimum": true,
              "type": "integer",
              "minimum": 0
            },
            "name": "user_id",
            "in": "query"
          },
          {
            "required": false,
            "schema": {
              "title": "Details",
              "type": "boolean",
              "default": true
            },
            "name": "details",
            "in": "query"
          },
          {
            "required": true,
            "schema": {
              "title": "X-Simcore-Products-Name",
              "type": "string"
            },
            "name": "x-simcore-products-name",
            "in": "header"
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "title": "Response List Services V0 Services Get",
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/ServiceOut"
                  }
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      }
    },
    "/v0/services/{service_key}/{service_version}/resources": {
      "get": {
        "tags": [
          "services"
        ],
        "summary": "Get Service Resources",
        "operationId": "get_service_resources_v0_services__service_key___service_version__resources_get",
        "parameters": [
          {
            "required": true,
            "schema": {
              "title": "Service Key",
              "pattern": "^(simcore)/(services)/(comp|dynamic|frontend)(/[\\w/-]+)+$",
              "type": "string"
            },
            "name": "service_key",
            "in": "path"
          },
          {
            "required": true,
            "schema": {
              "title": "Service Version",
              "pattern": "^(0|[1-9]\\d*)(\\.(0|[1-9]\\d*)){2}(-(0|[1-9]\\d*|\\d*[-a-zA-Z][-\\da-zA-Z]*)(\\.(0|[1-9]\\d*|\\d*[-a-zA-Z][-\\da-zA-Z]*))*)?(\\+[-\\da-zA-Z]+(\\.[-\\da-zA-Z-]+)*)?$",
              "type": "string"
            },
            "name": "service_version",
            "in": "path"
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ServiceResources"
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      }
    },
    "/v0/services/{service_key}/{service_version}": {
      "get": {
        "tags": [
          "services"
        ],
        "summary": "Get Service",
        "operationId": "get_service_v0_services__service_key___service_version__get",
        "parameters": [
          {
            "required": true,
            "schema": {
              "title": "Service Key",
              "pattern": "^(simcore)/(services)/(comp|dynamic|frontend)(/[\\w/-]+)+$",
              "type": "string"
            },
            "name": "service_key",
            "in": "path"
          },
          {
            "required": true,
            "schema": {
              "title": "Service Version",
              "pattern": "^(0|[1-9]\\d*)(\\.(0|[1-9]\\d*)){2}(-(0|[1-9]\\d*|\\d*[-a-zA-Z][-\\da-zA-Z]*)(\\.(0|[1-9]\\d*|\\d*[-a-zA-Z][-\\da-zA-Z]*))*)?(\\+[-\\da-zA-Z]+(\\.[-\\da-zA-Z-]+)*)?$",
              "type": "string"
            },
            "name": "service_version",
            "in": "path"
          },
          {
            "required": true,
            "schema": {
              "title": "User Id",
              "type": "integer"
            },
            "name": "user_id",
            "in": "query"
          },
          {
            "required": false,
            "schema": {
              "title": "X-Simcore-Products-Name",
              "type": "string"
            },
            "name": "x-simcore-products-name",
            "in": "header"
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ServiceOut"
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      },
      "patch": {
        "tags": [
          "services"
        ],
        "summary": "Modify Service",
        "operationId": "modify_service_v0_services__service_key___service_version__patch",
        "parameters": [
          {
            "required": true,
            "schema": {
              "title": "Service Key",
              "pattern": "^(simcore)/(services)/(comp|dynamic|frontend)(/[\\w/-]+)+$",
              "type": "string"
            },
            "name": "service_key",
            "in": "path"
          },
          {
            "required": true,
            "schema": {
              "title": "Service Version",
              "pattern": "^(0|[1-9]\\d*)(\\.(0|[1-9]\\d*)){2}(-(0|[1-9]\\d*|\\d*[-a-zA-Z][-\\da-zA-Z]*)(\\.(0|[1-9]\\d*|\\d*[-a-zA-Z][-\\da-zA-Z]*))*)?(\\+[-\\da-zA-Z]+(\\.[-\\da-zA-Z-]+)*)?$",
              "type": "string"
            },
            "name": "service_version",
            "in": "path"
          },
          {
            "required": true,
            "schema": {
              "title": "User Id",
              "type": "integer"
            },
            "name": "user_id",
            "in": "query"
          },
          {
            "required": false,
            "schema": {
              "title": "X-Simcore-Products-Name",
              "type": "string"
            },
            "name": "x-simcore-products-name",
            "in": "header"
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/ServiceUpdate"
              }
            }
          },
          "required": true
        },
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ServiceOut"
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "AccessEnum": {
        "title": "AccessEnum",
        "enum": [
          "ReadAndWrite",
          "Invisible",
          "ReadOnly"
        ],
        "type": "string",
        "description": "An enumeration."
      },
      "Author": {
        "title": "Author",
        "required": [
          "name",
          "email"
        ],
        "type": "object",
        "properties": {
          "name": {
            "title": "Name",
            "type": "string",
            "description": "Name of the author",
            "example": "Jim Knopf"
          },
          "email": {
            "title": "Email",
            "type": "string",
            "description": "Email address",
            "format": "email"
          },
          "affiliation": {
            "title": "Affiliation",
            "type": "string",
            "description": "Affiliation of the author"
          }
        },
        "additionalProperties": false
      },
      "Badge": {
        "title": "Badge",
        "required": [
          "name",
          "image",
          "url"
        ],
        "type": "object",
        "properties": {
          "name": {
            "title": "Name",
            "type": "string",
            "description": "Name of the subject"
          },
          "image": {
            "title": "Image",
            "maxLength": 2083,
            "minLength": 1,
            "type": "string",
            "description": "Url to the badge",
            "format": "uri"
          },
          "url": {
            "title": "Url",
            "maxLength": 2083,
            "minLength": 1,
            "type": "string",
            "description": "Link to the status",
            "format": "uri"
          }
        },
        "additionalProperties": false
      },
      "BootChoice": {
        "title": "BootChoice",
        "required": [
          "label",
          "description"
        ],
        "type": "object",
        "properties": {
          "label": {
            "title": "Label",
            "type": "string"
          },
          "description": {
            "title": "Description",
            "type": "string"
          }
        }
      },
      "BootOption": {
        "title": "BootOption",
        "required": [
          "label",
          "description",
          "default",
          "items"
        ],
        "type": "object",
        "properties": {
          "label": {
            "title": "Label",
            "type": "string"
          },
          "description": {
            "title": "Description",
            "type": "string"
          },
          "default": {
            "title": "Default",
            "type": "string"
          },
          "items": {
            "title": "Items",
            "type": "object",
            "additionalProperties": {
              "$ref": "#/components/schemas/BootChoice"
            }
          }
        }
      },
      "DAGIn": {
        "title": "DAGIn",
        "required": [
          "key",
          "version",
          "name"
        ],
        "type": "object",
        "properties": {
          "key": {
            "title": "Key",
            "pattern": "^(simcore)/(services)/(comp|dynamic|frontend)(/[\\w/-]+)+$",
            "type": "string",
            "example": "simcore/services/frontend/nodes-group/macros/1"
          },
          "version": {
            "title": "Version",
            "pattern": "^(0|[1-9]\\d*)(\\.(0|[1-9]\\d*)){2}(-(0|[1-9]\\d*|\\d*[-a-zA-Z][-\\da-zA-Z]*)(\\.(0|[1-9]\\d*|\\d*[-a-zA-Z][-\\da-zA-Z]*))*)?(\\+[-\\da-zA-Z]+(\\.[-\\da-zA-Z-]+)*)?$",
            "type": "string",
            "example": "1.0.0"
          },
          "name": {
            "title": "Name",
            "type": "string"
          },
          "description": {
            "title": "Description",
            "type": "string"
          },
          "contact": {
            "title": "Contact",
            "type": "string",
            "format": "email"
          },
          "workbench": {
            "title": "Workbench",
            "type": "object",
            "additionalProperties": {
              "$ref": "#/components/schemas/Node"
            }
          }
        }
      },
      "DAGOut": {
        "title": "DAGOut",
        "required": [
          "key",
          "version",
          "name",
          "id"
        ],
        "type": "object",
        "properties": {
          "key": {
            "title": "Key",
            "pattern": "^(simcore)/(services)/(comp|dynamic|frontend)(/[\\w/-]+)+$",
            "type": "string",
            "example": "simcore/services/frontend/nodes-group/macros/1"
          },
          "version": {
            "title": "Version",
            "pattern": "^(0|[1-9]\\d*)(\\.(0|[1-9]\\d*)){2}(-(0|[1-9]\\d*|\\d*[-a-zA-Z][-\\da-zA-Z]*)(\\.(0|[1-9]\\d*|\\d*[-a-zA-Z][-\\da-zA-Z]*))*)?(\\+[-\\da-zA-Z]+(\\.[-\\da-zA-Z-]+)*)?$",
            "type": "string",
            "example": "1.0.0"
          },
          "name": {
            "title": "Name",
            "type": "string"
          },
          "description": {
            "title": "Description",
            "type": "string"
          },
          "contact": {
            "title": "Contact",
            "type": "string",
            "format": "email"
          },
          "id": {
            "title": "Id",
            "type": "integer"
          },
          "workbench": {
            "title": "Workbench",
            "type": "object",
            "additionalProperties": {
              "$ref": "#/components/schemas/Node"
            }
          }
        }
      },
      "DatCoreFileLink": {
        "title": "DatCoreFileLink",
        "required": [
          "store",
          "path",
          "label",
          "dataset"
        ],
        "type": "object",
        "properties": {
          "store": {
            "title": "Store",
            "anyOf": [
              {
                "type": "string"
              },
              {
                "type": "integer"
              }
            ],
            "description": "The store identifier: '0' or 0 for simcore S3, '1' or 1 for datcore"
          },
          "path": {
            "title": "Path",
            "pattern": "^.+$",
            "type": "string",
            "description": "The path to the file in the storage provider domain"
          },
          "label": {
            "title": "Label",
            "type": "string",
            "description": "The real file name"
          },
          "eTag": {
            "title": "Etag",
            "type": "string",
            "description": "Entity tag that uniquely represents the file. The method to generate the tag is not specified (black box)."
          },
          "dataset": {
            "title": "Dataset",
            "type": "string",
            "description": "Unique identifier to access the dataset on datcore (REQUIRED for datcore)"
          }
        },
        "additionalProperties": false,
        "description": "I/O port type to hold a link to a file in DATCORE storage",
        "example": {
          "store": 1,
          "dataset": "N:dataset:ea2325d8-46d7-4fbd-a644-30f6433070b4",
          "path": "N:package:32df09ba-e8d6-46da-bd54-f696157de6ce",
          "label": "initial_WTstates"
        }
      },
      "DictModel_str__ResourceValue_": {
        "title": "DictModel[str, ResourceValue]",
        "type": "object",
        "additionalProperties": {
          "$ref": "#/components/schemas/ResourceValue"
        }
      },
      "DownloadLink": {
        "title": "DownloadLink",
        "required": [
          "downloadLink"
        ],
        "type": "object",
        "properties": {
          "downloadLink": {
            "title": "Downloadlink",
            "maxLength": 65536,
            "minLength": 1,
            "type": "string",
            "format": "uri"
          },
          "label": {
            "title": "Label",
            "type": "string"
          }
        },
        "additionalProperties": false,
        "description": "I/O port type to hold a generic download link to a file (e.g. S3 pre-signed link, etc)"
      },
      "HTTPValidationError": {
        "title": "HTTPValidationError",
        "type": "object",
        "properties": {
          "errors": {
            "title": "Validation errors",
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/ValidationError"
            }
          }
        }
      },
      "ImageResources": {
        "title": "ImageResources",
        "required": [
          "image",
          "resources"
        ],
        "type": "object",
        "properties": {
          "image": {
            "title": "Image",
            "pattern": "[\\w/-]+:[\\w.@]+",
            "type": "string",
            "description": "used by the fro"
          },
          "resources": {
            "$ref": "#/components/schemas/DictModel_str__ResourceValue_"
          }
        },
