info:
  contact: {email: frikky@shuffler.io, name: '@frikkylikeme', url: 'https://twitter.com/frikkylikeme'}
  description: Automated generation of Cisco Threat Grid
  title: Cisco Threat Grid
  version: 1.0.0
  x-logo: data:image/png;base64,
openapi: 3.0.2
paths:
  tmp0:
    post:
      description: Get artifact by it's hash
      parameters:
      - description: Lookup by hash/checksum
        example: 40dc213fe4551740e12cac575a9880753a9dacd510533f31bd7f635e743a7605
        in: query
        name: Hash
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: Threat Grid Get Artifact
  tmp1:
    post:
      description: Returns information for possible configuration changes to be made
        before analysis
      parameters:
      - description: Returns list of 'network-exits', 'playbooks', or 'vms'
        example: 'Input either ''network-exits'', ''playbooks'', or ''vms'' '
        in: query
        name: Configuration
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: Threat Grid Get Configuration(s)
  tmp10:
    post:
      description: Returns information for a registry key
      parameters:
      - description: Registry key to search for.
        example: MACHINE\Software\Microsoft\SystemCertificates\CA
        in: query
        name: Registry Key
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp10'}
          description: Successful request
      summary: Threat Grid Get Registry Key
  tmp11:
    post:
      description: Get sample(s) for analysis
      parameters: []
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp11'}
          description: Successful request
      summary: Threat Grid Get Sample(s)
  tmp12:
    post:
      description: Get sample(s) for analysis
      parameters: []
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp12'}
          description: Successful request
      summary: Threat Grid Get Session
  tmp13:
    post:
      description: Get User Details
      parameters:
      - description: Target login
        example: Username1
        in: query
        name: Login
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp13'}
          description: Successful request
      summary: Threat Grid Get User
  tmp14:
    post:
      description: Submit a sample for analysis
      parameters:
      - description: Sample to submit to ThreatGrid for analysis (from attachment
          in record)
        example: /path/to/malware.exe
        in: query
        name: Sample
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp14'}
          description: Successful request
      summary: Threat Grid Submit Sample
  tmp2:
    post:
      description: Returns information for a domain
      parameters:
      - description: Domain to search for.
        example: www.swimlane.com
        in: query
        name: Domain
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp2'}
          description: Successful request
      summary: Threat Grid Get Domain
  tmp3:
    post:
      description: Threat Grid content feeds are pre-generated, curated sets of behavioral
        indicators from sample analysis results
      parameters:
      - description: Name of the feed
        example: See API readme for comprehensive list of feed names
        in: query
        name: Name
        required: true
        schema: {type: string}
      - description: The response output format
        example: Input either 'json', 'csv', 'stix', 'snort'
        in: query
        name: Format
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp3'}
          description: Successful request
      summary: Threat Grid Get Feed
  tmp4:
    post:
      description: Provides an acknowledgement that the submission operation is healthy.
        If it's not, the result is a Service Unavailable error.
      parameters: []
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp4'}
          description: Successful request
      summary: Threat Grid Get Submission Health
  tmp5:
    post:
      description: Get Indicator Categories
      parameters: []
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp5'}
          description: Successful request
      summary: Threat Grid Get Indicator Categories
  tmp6:
    post:
      description: Get Information of  Indicators of Compromise (IOCs)
      parameters:
      - description: Name of indicator
        example: browser-firefox-extension
        in: query
        name: Indicator of Compromise
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp6'}
          description: Successful request
      summary: Threat Grid Get Indicators (IOC)
  tmp7:
    post:
      description: Returns information for an IP address
      parameters:
      - description: IP address to search for.
        example: 123.345.567.789
        in: query
        name: IP Address
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp7'}
          description: Successful request
      summary: Threat Grid Get IP
  tmp8:
    post:
      description: Retrieving organization properties
      parameters:
      - description: Target Organization ID
        example: '34204'
        in: query
        name: Organization ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp8'}
          description: Successful request
      summary: Threat Grid Get Organization
  tmp9:
    post:
      description: Returns information for a path
      parameters:
      - description: Path to search for.
        example: \Windows\rescache\rc0005\ResCache.hit
        in: query
        name: Path
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp9'}
          description: Successful request
      summary: Threat Grid Get Path
tags:
- {name: Network Security & Management}
