          "Version": {
            "allOf": [
              { "$ref": "#/components/schemas/String" },
              { "description": "The version of the application." }
            ]
          },
          "Args": {
            "allOf": [
              { "$ref": "#/components/schemas/StringList" },
              {
                "description": "Arguments for Amazon EMR to pass to the application."
              }
