{
  "openapi": "3.0.0",
  "info": {
    "title": "brapi - API de finanças",
    "description": "Tenha acesso instantâneo aos valores da cotação da bolsa de valores, dados históricos e até dados de criptomoedas em diferentes moedas\n\n**https://brapi.dev**",
    "termsOfService": "/terms-of-use",
    "contact": {
      "name": "brapi",
      "url": "https://brapi.dev/",
      "email": "brapi@proton.me"
    },
    "version": "2.4.1"
  },
  "servers": [
    {
      "url": "https://brapi.dev"
    }
  ],
  "paths": {
    "/api/quote/{tickers}": {
      "get": {
        "tags": ["ticker"],
        "summary": "Get any tickers informarion",
        "parameters": [
          {
            "name": "tickers",
            "in": "path",
            "description": "Add one or more tickers separated by a comma",
            "required": true,
            "schema": {
              "type": "string"
            },
            "example": "PETR4,MGLU3"
          },
          {
            "name": "range",
            "in": "query",
            "description": "Range for historical prices",
            "required": false,
            "style": "form",
            "schema": {
              "type": "string",
              "enum": [
                "1d",
                "5d",
                "1mo",
                "3mo",
                "6mo",
                "1y",
                "2y",
                "5y",
                "10y",
                "ytd",
                "max"
              ]
            },
            "example": ""
          },
          {
            "name": "interval",
            "in": "query",
            "description": "Interval to get historial prices within the range",
            "required": false,
            "style": "form",
            "schema": {
              "type": "string",
              "enum": [
                "1d",
                "5d",
                "1mo",
                "3mo",
                "6mo",
                "1y",
                "2y",
                "5y",
                "10y",
                "ytd",
                "max"
              ]
            },
            "example": ""
          },
          {
            "name": "fundamental",
            "in": "query",
            "description": "Retrieve fundamental analysis data",
            "required": false,
            "style": "form",
            "schema": {
              "type": "boolean"
            },
            "example": ""
          }
        ],
        "responses": {
          "200": {
            "description": "Successful response",
            "content": {
              "application/json": {
                "example": {
                  "results": [
                    {
                      "symbol": "COGN3",
                      "shortName": "COGNA ON    ON      NM",
                      "longName": "Cogna Educação S.A.",
                      "currency": "BRL",
                      "regularMarketPrice": 2.71,
                      "regularMarketDayHigh": 2.76,
                      "regularMarketDayLow": 2.67,
                      "regularMarketDayRange": "2.67 - 2.76",
                      "regularMarketChange": -0.029999971,
